{"tool":"newFileCreated","path":"dist/firebase-diagnostics.js","isOutsideWorkspace":false,"content":"// Firebase Advanced Diagnostics Script

// Global variables
let globalFirebaseApp = null;
let testResults = {
    browserInfo: {},
    configCheck: {},
    connectivityTests: {},
    cspAnalysis: {},
    firebaseInit: {},
    errors: []
};

// Initialize the diagnostic tool
document.addEventListener('DOMContentLoaded', function() {
    // Set up error monitoring
    setupErrorMonitoring();

    // Run initial diagnostics
    displayBrowserInfo();
    checkFirebaseConfig();
    analyzeCSP();

    // Set up button event listeners
    document.getElementById('run-connectivity-tests').addEventListener('click', runConnectivityTests);
    document.getElementById('test-global-config').addEventListener('click', () => testFirebaseInit('global'));
    document.getElementById('test-inline-config').addEventListener('click', () => testFirebaseInit('inline'));
    document.getElementById('test-fallback-config').addEventListener('click', () => testFirebaseInit('fallback'));
});

// Set up error monitoring
function setupErrorMonitoring() {
    const errorLogElement = document.getElementById('error-log');

    window.onerror = function(message, source, lineno, colno, error) {
        const errorTime = new Date().toISOString();
        const errorDetails = `[${errorTime}] ERROR: ${message}\nSource: ${source}\nLine: ${lineno}, Column: ${colno}\nStack: ${error ? error.stack : 'Not available'}`;

        // Log to console
        console.error(errorDetails);

        // Add to error log element
        errorLogElement.innerHTML += `<div class="error">${errorDetails}</div>`;

        // Add to test results
        testResults.errors.push({
            time: errorTime,
            message: message,
            source: source,
            lineno: lineno,
            colno: colno,
            stack: error ? error.stack : 'Not available'
        });

        // Update recommendations
        updateRecommendations();

        return true; // Prevent default error handling
    };

    // Also catch unhandled promise rejections
    window.addEventListener('unhandledrejection', function(event) {
        const errorTime = new Date().toISOString();
        const errorDetails = `[${errorTime}] UNHANDLED PROMISE REJECTION: ${event.reason}`;

        // Log to console
        console.error(errorDetails);

        // Add to error log element
        errorLogElement.innerHTML += `<div class="error">${errorDetails}</div>`;

        // Add to test results
        testResults.errors.push({
            time: errorTime,
            type: 'unhandledRejection',
            reason: event.reason
        });

        // Update recommendations
        updateRecommendations();
    });

    // Initial message
    errorLogElement.innerHTML = '<div class="info">Error monitoring is active. Any JavaScript errors will appear here.</div>';
}

// Display browser and environment information
function displayBrowserInfo() {
    const browserInfoElement = document.getElementById('browser-info');

    const info = {
        userAgent: navigator.userAgent,
        platform: navigator.platform,
        language: navigator.language,
        cookiesEnabled: navigator.cookieEnabled,
        windowDimensions: `${window.innerWidth}x${window.innerHeight}`,
        screenDimensions: `${window.screen.width}x${window.screen.height}`,
        pixelRatio: window.devicePixelRatio,
        timeZone: Intl.DateTimeFormat().resolvedOptions().timeZone,
        currentTime: new Date().toString()
    };

    // Store in test results
    testResults.browserInfo = info;

    // Display in the browser info element
    let infoHtml = '';
    for (const [key, value] of Object.entries(info)) {
        infoHtml += `<strong>${key}:</strong> ${value}<br>`;
    }

    browserInfoElement.innerHTML = infoHtml;
}

// Check Firebase configuration
function checkFirebaseConfig() {
    const configCheckElement = document.getElementById('firebase-config-check');
    configCheckElement.innerHTML = '<div class="info">Checking Firebase configuration...</div>';

    let configHtml = '';

    // Check if window.FIREBASE_CONFIG exists
    const firebaseConfigExists = typeof window !== 'undefined' && window.FIREBASE_CONFIG;
    configHtml += `<div class="${firebaseConfigExists ? 'success' : 'error'}">window.FIREBASE_CONFIG exists: ${firebaseConfigExists ? 'Yes' : 'No'}</div>`;

    if (firebaseConfigExists) {
        // Check if all required Firebase configuration properties are present
        const requiredProps = ['apiKey', 'authDomain', 'projectId', 'storageBucket', 'messagingSenderId', 'appId'];
        const missingProps = requiredProps.filter(prop => !window.FIREBASE_CONFIG[prop]);

        if (missingProps.length === 0) {
            configHtml += '<div class="success">All required Firebase configuration properties are present.</div>';
        } else {
            configHtml += `<div class="error">Missing Firebase configuration properties: ${missingProps.join(', ')}</div>`;
        }

        // Display the configuration status (without exposing sensitive values)
        configHtml += '<div class="info">Firebase Configuration Status:</div>';
        configHtml += '<table>';
        configHtml += '<tr><th>Property</th><th>Status</th></tr>';

        for (const prop of requiredProps) {
            const propExists = !!window.FIREBASE_CONFIG[prop];
            configHtml += `<tr>
                <td>${prop}</td>
                <td class="${propExists ? 'success' : 'error'}">${propExists ? 'Defined' : 'Undefined'}</td>
            </tr>`;
        }

        configHtml += '</table>';

        // Store in test results
        testResults.configCheck = {
            exists: true,
            missingProps: missingProps,
            properties: requiredProps.reduce((acc, prop) => {
                acc[prop] = !!window.FIREBASE_CONFIG[prop];
                return acc;
            }, {})
        };
    } else {
        configHtml += '<div class="error">Firebase configuration is not available. Please check that firebase-config.js is properly loaded before this script.</div>';

        // Store in test results
        testResults.configCheck = {
            exists: false
        };
    }

    configCheckElement.innerHTML = configHtml;

    // Update recommendations based on config check
    updateRecommendations();
}

// Run connectivity tests to Firebase domains
function runConnectivityTests() {
    const connectivityElement = document.getElementById('connectivity-results');
    connectivityElement.innerHTML = '<div class="info">Running connectivity tests...</div>';

    // List of Firebase domains to test
    const domains = [
        'firebaseinstallations.googleapis.com',
        'firestore.googleapis.com',
        'firebase.googleapis.com',
        'firebaseremoteconfig.googleapis.com',
        'firebasestorage.googleapis.com',
        'identitytoolkit.googleapis.com',
        'www.googleapis.com',
        'www.gstatic.com'
    ];

    let testsCompleted = 0;
    let testResults = {};
    let resultsHtml = '';

    // Function to update the results display
    function updateResults() {
        let html = '<table>';
        html += '<tr><th>Domain</th><th>Status</th><th>Time (ms)</th></tr>';

        for (const domain in testResults) {
            const result = testResults[domain];
            html += `<tr>
                <td>${domain}</td>
                <td class="${result.success ? 'success' : 'error'}">${result.success ? 'Success' : 'Failed'}</td>
                <td>${result.time !== undefined ? result.time : 'N/A'}</td>
            </tr>`;
        }

        html += '</table>';

        if (testsCompleted < domains.length) {
            html += `<div class="info">Testing... ${testsCompleted} of ${domains.length} completed</div>`;
        } else {
            html += '<div class="success">All tests completed</div>';
        }

        connectivityElement.innerHTML = html;
    }

    // Test each domain
    domains.forEach(domain => {
        testResults[domain] = { success: false };

        const startTime = performance.now();
        const img = new Image();

        // Set a timeout for the test
        const timeout = setTimeout(() => {
            img.onload = img.onerror = null;
            testResults[domain].success = false;
            testResults[domain].time = 'Timeout';
            testsCompleted++;
            updateResults();
        }, 5000);

        img.onload = function() {
            clearTimeout(timeout);
            const endTime = performance.now();
            testResults[domain].success = true;
            testResults[domain].time = Math.round(endTime - startTime);
            testsCompleted++;
            updateResults();
        };

        img.onerror = function() {
            clearTimeout(timeout);
            const endTime = performance.now();
            // For image loading, an error doesn't necessarily mean the domain is unreachable
            // It could just mean the endpoint doesn't serve images
            testResults[domain].success = true; // Consider it a success if we got a response
            testResults[domain].time = Math.round(endTime - startTime);
            testsCompleted++;
            updateResults();
        };

        // Attempt to load a favicon or other small resource from the domain
        img.src = `https://${domain}/favicon.ico?nocache=${new Date().getTime()}`;
    });

    // Also test with fetch API for more accurate results
    domains.forEach(domain => {
        const startTime = performance.now();

        fetch(`https://${domain}/favicon.ico?nocache=${new Date().getTime()}`, {
            mode: 'no-cors', // This allows the request to be sent without requiring CORS headers
            cache: 'no-cache'
        })
        .then(() => {
            const endTime = performance.now();
            // Update the existing result if it's better
            if (!testResults[domain].success || testResults[domain].time === 'Timeout') {
                testResults[domain].success = true;
                testResults[domain].time = Math.round(endTime - startTime);
                updateResults();
            }
        })
        .catch(error => {
            // If we get a network error, the domain might be blocked
            console.error(`Fetch error for ${domain}:`, error);
            // Only update if we don't already have a successful result
            if (!testResults[domain].success) {
                testResults[domain].fetchError = error.message;
                updateResults();
            }
        });
    });

    // Store in global test results
    window.testResults.connectivityTests = testResults;

    // Initial update
    updateResults();
}

// Analyze Content Security Policy
function analyzeCSP() {
    const cspElement = document.getElementById('csp-analysis');
    cspElement.innerHTML = '<div class="info">Analyzing Content Security Policy...</div>';

    // Get the CSP from the meta tag
    let cspContent = '';
    const cspMetaTag = document.querySelector('meta[http-equiv="Content-Security-Policy"]');

    if (cspMetaTag) {
        cspContent = cspMetaTag.getAttribute('content');
    }

    // Also try to get it from the HTTP headers
    let cspFromHeaders = '';

    // We can't directly access HTTP headers from JavaScript, but we can make an XHR request to the current page
    // and check the headers in the response
    const xhr = new XMLHttpRequest();
    xhr.open('GET', window.location.href, true);
    xhr.onreadystatechange = function() {
        if (xhr.readyState === 4) {
            const cspHeader = xhr.getResponseHeader('Content-Security-Policy');
            if (cspHeader) {
                cspFromHeaders = cspHeader;
                analyzeCSPContent(cspFromHeaders, 'HTTP Header');
            }
        }
    };
    xhr.send();

    // Analyze the CSP content from the meta tag
    if (cspContent) {
        analyzeCSPContent(cspContent, 'Meta Tag');
    } else {
        cspElement.innerHTML += '<div class="warning">No Content-Security-Policy meta tag found.</div>';
    }

    // Function to analyze CSP content
    function analyzeCSPContent(csp, source) {
        cspElement.innerHTML += `<div class="info">CSP from ${source}:</div>`;
        cspElement.innerHTML += `<div class="log-container">${csp}</div>`;

        // Parse the CSP
        const policies = {};
        csp.split(';').forEach(policy => {
            const trimmedPolicy = policy.trim();
            if (trimmedPolicy) {
                const [directive, ...sources] = trimmedPolicy.split(/\s+/);
                policies[directive] = sources;
            }
        });

        // Check for Firebase-related directives
        const firebaseDomains = [
            'firebaseinstallations.googleapis.com',
            'firestore.googleapis.com',
            'firebase.googleapis.com',
            'firebaseremoteconfig.googleapis.com',
