        if (initResults.fallback && initResults.fallback.success && (!initResults.global || !initResults.global.success)) {
            recommendations.push({
                priority: 'medium',
                message: 'Minimal fallback configuration works but global configuration doesn\'t. Consider simplifying your Firebase configuration to include only the essential fields.'
            });
        }
    }

    // Check for errors
    if (testResults.errors.length > 0) {
