        if (uniqueErrors.length > 0) {
            recommendations.push({
                priority: 'high',
                message: `JavaScript errors detected: ${uniqueErrors.join('; ')}. These errors may be preventing the application from loading properly.`
            });
        }
    }

    // Add GoDaddy-specific recommendations
    recommendations.push({
        priority: 'medium',
        message: 'GoDaddy shared hosting may have limitations that affect Firebase. Consider the following GoDaddy-specific recommendations:\n- Check if outbound connections to Firebase domains are being blocked\n- Verify that your .htaccess file is properly uploaded and not modified by the server\n- Consider using a simpler Firebase configuration with only the essential services'
    });

    // If no specific recommendations, add general ones
    if (recommendations.length === 0) {
        recommendations.push({
            priority: 'low',
            message: 'No specific issues detected. Run the connectivity tests and Firebase initialization tests to gather more information.'
        });
    }

    // Sort recommendations by priority
    recommendations.sort((a, b) => {
        const priorityOrder = { high: 0, medium: 1, low: 2 };
        return priorityOrder[a.priority] - priorityOrder[b.priority];
    });

    // Display recommendations
    let recommendationsHtml = '';

    recommendations.forEach(rec => {
        const priorityClass = rec.priority === 'high' ? 'error' : (rec.priority === 'medium' ? 'warning' : 'info');
        recommendationsHtml += `<div class="${priorityClass}"><strong>${rec.priority.toUpperCase()}:</strong> ${rec.message}</div>`;
