284 |         try {
285 |           // Create payment intent on the server
286 |           const response = await fetch('/api/create-payment', {
287 |             method: 'POST',
288 |             headers: {
289 |               'Content-Type': 'application/json'
290 |             },
291 |             body: JSON.stringify({
292 |               userId: 'test_user_' + Math.random().toString(36).substring(2),
293 |               productId: 'test_product_' + Math.random().toString(36).substring(2),
294 |               productName: 'Test Product',
