297 |               customerDetails: {
298 |                 name,
299 |                 email,
300 |                 address: {
301 |                   line1: address,
302 |                   city,
303 |                   state,
304 |                   postal_code: zip,
305 |                   country
306 |                 }
307 |               }
308 |             })
309 |           });
310 |           
311 |           const data = await response.json();
312 |           
313 |           if (data.error) {
314 |             showError(data.error);
315 |             return;
316 |           }
