318 |           // Confirm card payment
319 |           const { error, paymentIntent } = await stripe.confirmCardPayment(data.clientSecret, {
320 |             payment_method: {
321 |               card: cardElement,
322 |               billing_details: {
323 |                 name,
324 |                 email,
325 |                 address: {
326 |                   line1: address,
327 |                   city,
328 |                   state,
329 |                   postal_code: zip,
330 |                   country
331 |                 }
332 |               }
333 |             }
334 |           });
