+ */
+const isLevelAtLeast = (level, minLevel) => {
+  const levelOrder = [
+    LogLevel.TRACE,
+    LogLevel.DEBUG,
+    LogLevel.INFO,
+    LogLevel.WARN,
+    LogLevel.ERROR,
+    LogLevel.FATAL,
+  ];
+  
+  const levelIndex = levelOrder.indexOf(level);
+  const minLevelIndex = levelOrder.indexOf(minLevel);
+  
+  return levelIndex >= minLevelIndex;
+};

+/**
