+export const createError = (message, code, details = {}) => {
+  const error = new Error(message);
+  error.code = code;
+  error.details = details;
+  return error;
+};

+/**
+ * Parse error from various sources
+ * 
+ * @param {any} error - Error to parse
+ * @returns {Error} Standardized error
