+    if (error.details) parsedError.details = error.details;
+    if (error.name) parsedError.name = error.name;
+    if (error.stack) parsedError.stack = error.stack;
+    return parsedError;
+  }
+  
+  return new Error(JSON.stringify(error));
+};

