 * Environment Validator Atom
 * 
 * Provides primitive functions for validating environment variables.
 */

// Required environment variables grouped by category
const REQUIRED_ENV_VARS = {
  firebase: [
    'FIREBASE_API_KEY',
    'FIREBASE_AUTH_DOMAIN',
    'FIREBASE_PROJECT_ID',
    'FIREBASE_STORAGE_BUCKET',
    'FIREBASE_MESSAGING_SENDER_ID',
    'FIREBASE_APP_ID'
  ],
  api: [
    'ODDS_API_KEY',
    'STRIPE_SECRET_KEY',
    'FANDUEL_AFFILIATE_ID',
    'FANDUEL_API_KEY'
  ],
  app: [
    'APP_ENV',
    'API_BASE_URL'
  ]
};

/**
 * Check if required environment variables are set
 * @returns {Object} Result of the check with missing variables
 */
export function checkEnvVars() {
  const missing = {};
  let hasErrors = false;

  // Check each category
  Object.entries(REQUIRED_ENV_VARS).forEach(([category, vars]) => {
    const missingInCategory = vars.filter(varName => !process.env[varName]);

    if (missingInCategory.length > 0) {
      missing[category] = missingInCategory;
      hasErrors = true;
    }
  });

  return {
    success: !hasErrors,
    missing
  };
}

/**
 * Get missing environment variables message
 * @param {Object} missing - Missing variables by category
 * @returns {string} Formatted message
 */
export function getMissingEnvVarsMessage(missing) {
  let message = 'Missing required environment variables:\n';

  Object.entries(missing).forEach(([category, vars]) => {
    message += `\n${category.toUpperCase()}:\n`;
    vars.forEach(varName => {
      message += `  - ${varName}\n`;
    });
  });

  return message;
}

/**
 * Get category-specific instructions
 * @param {Object} missing - Missing variables by category
 * @returns {string} Instructions message
 */
export function getCategoryInstructions(missing) {
  let message = '';

  if (missing.firebase) {
    message += 'Firebase config is incomplete. Check firebase-config directory for setup instructions.\n';
  }

  if (missing.api) {
    message += 'API keys are missing. See .env.example for required keys.\n';
  }

  return message;
}"}

{"tool":"newFileCreated","path":"atomic/molecules/environmentValidator.js","isOutsideWorkspace":false,"content":"/**
 * Environment Validator Molecule
 * 
 * Combines atomic environment validation utilities to provide
 * a complete environment validation solution.
 */

import { checkEnvVars, getMissingEnvVarsMessage, getCategoryInstructions } from '../atoms/envValidator';
import { validateConfig } from '../atoms/envConfig';
import { isDevelopment } from '../atoms/envConfig';

/**
 * Validate environment at startup
 * @param {Object} options - Validation options
 * @param {boolean} options.exitOnError - Whether to exit the process on error
 * @param {boolean} options.logResults - Whether to log validation results
 * @returns {boolean} Whether validation passed
 */
export function validateEnvironment(options = { exitOnError: true, logResults: true }) {
  if (options.logResults) {
    console.log('Validating environment variables...');
  }

  const result = checkEnvVars();

  if (!result.success) {
    // Get detailed error messages
    const missingVarsMessage = getMissingEnvVarsMessage(result.missing);
    const categoryInstructions = getCategoryInstructions(result.missing);

    // Log error messages
    console.error(missingVarsMessage);
    console.error(categoryInstructions);

    // Exit if required
    if (options.exitOnError) {
      console.error('Exiting due to missing environment variables');
      process.exit(1);
    }

    return false;
  }

  if (options.logResults) {
    console.log('Environment validation passed');
  }

  return true;
}

/**
 * Validate service configuration
 * @param {Object} config - Configuration object to validate
 * @param {Array<string>} requiredKeys - Required keys in the configuration
 * @param {string} serviceName - Name of the service for logging
 * @param {Object} options - Validation options
 * @param {boolean} options.exitOnError - Whether to exit the process on error
 * @param {boolean} options.logResults - Whether to log validation results
 * @returns {boolean} Whether validation passed
 */
export function validateServiceConfig(
  config, 
  requiredKeys, 
  serviceName, 
  options = { exitOnError: false, logResults: true }
) {
  if (options.logResults) {
    console.log(`Validating ${serviceName} configuration...`);
  }

  const isValid = validateConfig(config, requiredKeys);

  if (!isValid) {
    const missingKeys = requiredKeys.filter(key => !config[key]);
    console.error(`${serviceName} configuration is incomplete. Missing: ${missingKeys.join(', ')}`);

    if (options.exitOnError) {
      console.error(`Exiting due to invalid ${serviceName} configuration`);
      process.exit(1);
    }

    return false;
  }

  if (options.logResults) {
    console.log(`${serviceName} configuration validation passed`);
  }

  return true;
}

