{"tool":"newFileCreated","path":"scripts/format-atomic.js","isOutsideWorkspace":false,"content":"/**
 * Format Atomic Components Script
 * 
 * This script formats all files in the atomic directory structure
 * using the project's Prettier configuration.
 */

const { execSync } = require('child_process');
const path = require('path');
const fs = require('fs');

// Configuration
const ATOMIC_DIR = path.join(__dirname, '..', 'atomic');
const PRETTIER_CONFIG = path.join(__dirname, '..', '.prettierrc');

// Ensure the atomic directory exists
if (!fs.existsSync(ATOMIC_DIR)) {
  console.error(`Error: Atomic directory not found at ${ATOMIC_DIR}`);
  process.exit(1);
}

// Ensure the Prettier config exists
if (!fs.existsSync(PRETTIER_CONFIG)) {
  console.error(`Error: Prettier config not found at ${PRETTIER_CONFIG}`);
  process.exit(1);
}

/**
 * Format files in a directory
 * @param {string} dir - Directory to format
 */
function formatDirectory(dir) {
  try {
    console.log(`Formatting files in ${dir}...`);

    // Format all JavaScript and TypeScript files in the directory
    const command = `npx prettier --config ${PRETTIER_CONFIG} --write "${dir}/**/*.{js,jsx,ts,tsx}"`;
    execSync(command, { stdio: 'inherit' });

    console.log(`Successfully formatted files in ${dir}`);
  } catch (error) {
    console.error(`Error formatting files in ${dir}:`, error.message);
  }
}

// Format each atomic level
const atomicLevels = ['atoms', 'molecules', 'organisms', 'templates', 'pages'];

atomicLevels.forEach(level => {
  const levelDir = path.join(ATOMIC_DIR, level);

  if (fs.existsSync(levelDir)) {
    formatDirectory(levelDir);
  } else {
    console.log(`Skipping ${level} - directory not found`);
  }
});

console.log('All atomic components formatted successfully!');"}

{"tool":"newFileCreated","path":"atomic/molecules/firebaseFirestore.js","isOutsideWorkspace":false,"content":"/**
 * Firebase Firestore Molecule
 * 
 * Provides Firebase Firestore database functionality.
 * Combines the Firebase app atom with Firestore-specific features.
 */

import {
  getFirestore,
  collection,
  doc,
  addDoc,
  setDoc,
  updateDoc,
  deleteDoc,
  getDoc,
  getDocs,
  query,
  where,
  orderBy,
  limit,
  startAfter,
  onSnapshot,
  enableIndexedDbPersistence,
  disableNetwork,
  enableNetwork,
  serverTimestamp,
  Timestamp,
  writeBatch
} from 'firebase/firestore';
import { getFirebaseApp } from '../atoms/firebaseApp';

// Firebase Firestore instance (singleton)
let db = null;

/**
 * Initialize Firebase Firestore
 * @param {Object} options - Firestore initialization options
 * @param {boolean} options.enablePersistence - Whether to enable offline persistence
 * @returns {Object|null} Firebase Firestore instance or null if initialization failed
 */
export const initializeFirestore = (options = { enablePersistence: true }) => {
  try {
    // Get Firebase app instance
    const app = getFirebaseApp();
    if (!app) {
      console.error('Firestore initialization failed: Firebase app not initialized');
      return null;
    }

    // Initialize Firestore
    db = getFirestore(app);

    // Enable offline persistence if requested
    if (options.enablePersistence) {
      enableIndexedDbPersistence(db)
        .then(() => {
          console.log('Firestore persistence enabled successfully');
        })
        .catch((error) => {
          if (error.code === 'failed-precondition') {
            console.warn(
              'Firestore persistence could not be enabled: Multiple tabs open. ' +
              'Persistence can only be enabled in one tab at a time.'
            );
          } else if (error.code === 'unimplemented') {
            console.warn(
              'Firestore persistence could not be enabled: The current browser does not support all of the features required to enable persistence.'
            );
          } else {
            console.error('Error enabling Firestore persistence:', error);
          }
        });
    }

    console.log('Firestore initialized successfully');
    return db;
  } catch (error) {
    console.error('Error initializing Firestore:', error);
    return null;
  }
};

/**
 * Get Firebase Firestore instance
 * Initializes Firestore if it hasn't been initialized yet
 * 
 * @returns {Object|null} Firebase Firestore instance or null if initialization failed
 */
export const getFirestoreDb = () => {
  if (!db) {
    return initializeFirestore();
  }
  return db;
};

/**
 * Create a document in a collection
 * @param {string} collectionPath - Collection path
 * @param {Object} data - Document data
 * @param {Object} options - Options
 * @param {boolean} options.addTimestamp - Whether to add a timestamp
 * @returns {Promise<string>} Document ID
 */
export const createDocument = async (
  collectionPath,
  data,
  options = { addTimestamp: true }
) => {
  const dbInstance = getFirestoreDb();
  if (!dbInstance) {
    throw new Error('Firestore not initialized');
  }

  try {
    const documentData = { ...data };

    if (options.addTimestamp) {
      documentData.createdAt = serverTimestamp();
      documentData.updatedAt = serverTimestamp();
    }

    const docRef = await addDoc(collection(dbInstance, collectionPath), documentData);
    return docRef.id;
  } catch (error) {
    console.error('Error creating document:', error);
    throw error;
  }
};

/**
 * Set a document with a specific ID
 * @param {string} collectionPath - Collection path
 * @param {string} documentId - Document ID
 * @param {Object} data - Document data
 * @param {Object} options - Options
 * @param {boolean} options.merge - Whether to merge with existing data
 * @param {boolean} options.addTimestamp - Whether to add a timestamp
 * @returns {Promise<void>}
 */
export const setDocument = async (
  collectionPath,
  documentId,
  data,
  options = { merge: true, addTimestamp: true }
) => {
  const dbInstance = getFirestoreDb();
  if (!dbInstance) {
    throw new Error('Firestore not initialized');
  }

  try {
    const documentData = { ...data };

    if (options.addTimestamp) {
      if (!documentData.createdAt) {
        documentData.createdAt = serverTimestamp();
      }
      documentData.updatedAt = serverTimestamp();
    }

    await setDoc(
      doc(dbInstance, collectionPath, documentId),
      documentData,
      { merge: options.merge }
    );
  } catch (error) {
    console.error('Error setting document:', error);
    throw error;
  }
};

/**
 * Update a document
 * @param {string} collectionPath - Collection path
 * @param {string} documentId - Document ID
 * @param {Object} data - Document data
 * @param {Object} options - Options
 * @param {boolean} options.addTimestamp - Whether to add a timestamp
 * @returns {Promise<void>}
 */
export const updateDocument = async (
  collectionPath,
  documentId,
  data,
  options = { addTimestamp: true }
) => {
  const dbInstance = getFirestoreDb();
  if (!dbInstance) {
    throw new Error('Firestore not initialized');
  }

  try {
    const documentData = { ...data };

    if (options.addTimestamp) {
      documentData.updatedAt = serverTimestamp();
    }

    await updateDoc(
      doc(dbInstance, collectionPath, documentId),
      documentData
    );
  } catch (error) {
    console.error('Error updating document:', error);
    throw error;
  }
};

/**
 * Delete a document
 * @param {string} collectionPath - Collection path
 * @param {string} documentId - Document ID
 * @returns {Promise<void>}
 */
export const deleteDocument = async (collectionPath, documentId) => {
  const dbInstance = getFirestoreDb();
  if (!dbInstance) {
    throw new Error('Firestore not initialized');
  }

  try {
    await deleteDoc(doc(dbInstance, collectionPath, documentId));
  } catch (error) {
    console.error('Error deleting document:', error);
    throw error;
  }
};

/**
 * Get a document
 * @param {string} collectionPath - Collection path
 * @param {string} documentId - Document ID
 * @returns {Promise<Object|null>} Document data or null if not found
 */
export const getDocument = async (collectionPath, documentId) => {
  const dbInstance = getFirestoreDb();
  if (!dbInstance) {
    throw new Error('Firestore not initialized');
  }

  try {
    const docSnap = await getDoc(doc(dbInstance, collectionPath, documentId));

    if (docSnap.exists()) {
      return { id: docSnap.id, ...docSnap.data() };
    } else {
      return null;
    }
  } catch (error) {
    console.error('Error getting document:', error);
    throw error;
  }
};

/**
 * Query documents
 * @param {string} collectionPath - Collection path
 * @param {Array} conditions - Query conditions
 * @param {Array} orderByFields - Order by fields
 * @param {number} limitCount - Limit count
 * @param {Object} startAfterDoc - Start after document
 * @returns {Promise<Array>} Documents
 */
export const queryDocuments = async (
  collectionPath,
  conditions = [],
  orderByFields = [],
  limitCount = 0,
  startAfterDoc = null
) => {
  const dbInstance = getFirestoreDb();
  if (!dbInstance) {
    throw new Error('Firestore not initialized');
  }

  try {
    let q = collection(dbInstance, collectionPath);

    // Apply conditions
    if (conditions.length > 0) {
      const queryConstraints = conditions.map(condition => {
        const [field, operator, value] = condition;
        return where(field, operator, value);
      });

      q = query(q, ...queryConstraints);
    }

    // Apply order by
    if (orderByFields.length > 0) {
      const orderByConstraints = orderByFields.map(field => {
        if (typeof field === 'string') {
          return orderBy(field);
        } else {
          const [fieldName, direction] = field;
          return orderBy(fieldName, direction);
        }
      });

      q = query(q, ...orderByConstraints);
    }

    // Apply start after
    if (startAfterDoc) {
      q = query(q, startAfter(startAfterDoc));
    }

    // Apply limit
    if (limitCount > 0) {
      q = query(q, limit(limitCount));
    }

    const querySnapshot = await getDocs(q);

    const documents = [];
    querySnapshot.forEach(doc => {
      documents.push({ id: doc.id, ...doc.data() });
    });

    return documents;
  } catch (error) {
    console.error('Error querying documents:', error);
    throw error;
  }
};

/**
 * Subscribe to document changes
 * @param {string} collectionPath - Collection path
 * @param {string} documentId - Document ID
 * @param {Function} callback - Callback function
 * @returns {Function} Unsubscribe function
 */
export const subscribeToDocument = (collectionPath, documentId, callback) => {
  const dbInstance = getFirestoreDb();
  if (!dbInstance) {
    throw new Error('Firestore not initialized');
  }

  try {
    const unsubscribe = onSnapshot(
      doc(dbInstance, collectionPath, documentId),
      (docSnapshot) => {
        if (docSnapshot.exists()) {
          callback({ id: docSnapshot.id, ...docSnapshot.data() });
        } else {
          callback(null);
        }
      },
      (error) => {
        console.error('Error subscribing to document:', error);
        callback(null, error);
      }
    );

    return unsubscribe;
  } catch (error) {
    console.error('Error setting up document subscription:', error);
    throw error;
  }
};

/**
 * Subscribe to query changes
 * @param {string} collectionPath - Collection path
 * @param {Array} conditions - Query conditions
 * @param {Array} orderByFields - Order by fields
 * @param {number} limitCount - Limit count
 * @param {Function} callback - Callback function
 * @returns {Function} Unsubscribe function
 */
export const subscribeToQuery = (
  collectionPath,
  conditions = [],
  orderByFields = [],
  limitCount = 0,
  callback
) => {
  const dbInstance = getFirestoreDb();
  if (!dbInstance) {
    throw new Error('Firestore not initialized');
  }

  try {
    let q = collection(dbInstance, collectionPath);

    // Apply conditions
    if (conditions.length > 0) {
      const queryConstraints = conditions.map(condition => {
        const [field, operator, value] = condition;
        return where(field, operator, value);
      });

      q = query(q, ...queryConstraints);
    }

    // Apply order by
    if (orderByFields.length > 0) {
      const orderByConstraints = orderByFields.map(field => {
        if (typeof field === 'string') {
          return orderBy(field);
        } else {
          const [fieldName, direction] = field;
          return orderBy(fieldName, direction);
        }
      });

      q = query(q, ...orderByConstraints);
    }

    // Apply limit
    if (limitCount > 0) {
      q = query(q, limit(limitCount));
    }

    const unsubscribe = onSnapshot(
      q,
      (querySnapshot) => {
        const documents = [];
        querySnapshot.forEach(doc => {
          documents.push({ id: doc.id, ...doc.data() });
        });

        callback(documents);
      },
      (error) => {
        console.error('Error subscribing to query:', error);
        callback([], error);
      }
    );

    return unsubscribe;
  } catch (error) {
    console.error('Error setting up query subscription:', error);
    throw error;
  }
};

/**
 * Create a batch write operation
 * @returns {Object} Batch write object
 */
export const createBatch = () => {
  const dbInstance = getFirestoreDb();
  if (!dbInstance) {
    throw new Error('Firestore not initialized');
  }

  return writeBatch(dbInstance);
};

/**
 * Disable network (work offline)
 * @returns {Promise<void>}
 */
export const goOffline = async () => {
  const dbInstance = getFirestoreDb();
  if (!dbInstance) {
    throw new Error('Firestore not initialized');
  }

  try {
    await disableNetwork(dbInstance);
    console.log('Firestore network disabled');
  } catch (error) {
    console.error('Error disabling Firestore network:', error);
    throw error;
  }
};

/**
 * Enable network (work online)
 * @returns {Promise<void>}
 */
export const goOnline = async () => {
  const dbInstance = getFirestoreDb();
  if (!dbInstance) {
    throw new Error('Firestore not initialized');
  }

  try {
    await enableNetwork(dbInstance);
    console.log('Firestore network enabled');
  } catch (error) {
    console.error('Error enabling Firestore network:', error);
    throw error;
  }
};

/**
 * Get server timestamp
 * @returns {Object} Server timestamp
 */
export const getServerTimestamp = () => {
  return serverTimestamp();
};

/**
 * Create a timestamp from date
 * @param {Date} date - Date
 * @returns {Object} Timestamp
 */
export const createTimestamp = (date) => {
  return Timestamp.fromDate(date);
};

// Initialize Firestore on module load
initializeFirestore();

// Export Firestore instance
export { db };"}

{"tool":"newFileCreated","path":"atomic/organisms/firebaseService.js","isOutsideWorkspace":false,"content":"/**
 * Firebase Service Organism
 * 
 * Integrates all Firebase functionality into a cohesive service.
 * This organism combines Firebase app, auth, and Firestore molecules
 * to provide a complete Firebase solution.
 */

import { getFirebaseApp, initializeFirebaseApp } from '../atoms/firebaseApp';
import {
  getFirebaseAuth,
  initializeFirebaseAuth,
  signIn,
  createUser,
  logOut,
  resetPassword,
  updateUserProfile,
  updateUserEmail,
  updateUserPassword,
  getCurrentUser
} from '../molecules/firebaseAuth';
import {
  getFirestoreDb,
  initializeFirestore,
  createDocument,
  setDocument,
  updateDocument,
  deleteDocument,
  getDocument,
  queryDocuments,
  subscribeToDocument,
  subscribeToQuery,
  createBatch,
  goOffline,
  goOnline,
  getServerTimestamp,
  createTimestamp
} from '../molecules/firebaseFirestore';

/**
 * Firebase service initialization options
 * @typedef {Object} FirebaseInitOptions
 * @property {boolean} enablePersistence - Whether to enable Firestore offline persistence
 * @property {boolean} enableAuthPersistence - Whether to enable Auth persistence
 * @property {boolean} enableLogging - Whether to enable logging
 */

/**
 * Initialize Firebase services
 * @param {FirebaseInitOptions} options - Initialization options
 * @returns {Object} Initialization result
 */
export const initializeFirebase = (options = {
  enablePersistence: true,
  enableAuthPersistence: true,
  enableLogging: true
}) => {
  const result = {
    success: false,
    app: null,
    auth: null,
    firestore: null,
    errors: []
  };

  try {
    // Initialize Firebase app
    if (options.enableLogging) {
      console.log('Initializing Firebase app...');
    }

    result.app = initializeFirebaseApp();

    if (!result.app) {
      result.errors.push('Failed to initialize Firebase app');
      return result;
    }

    if (options.enableLogging) {
      console.log('Firebase app initialized successfully');
    }

    // Initialize Firebase Auth
    if (options.enableLogging) {
      console.log('Initializing Firebase Auth...');
    }

    result.auth = initializeFirebaseAuth();

    if (!result.auth) {
      result.errors.push('Failed to initialize Firebase Auth');
    } else if (options.enableLogging) {
      console.log('Firebase Auth initialized successfully');
    }

    // Initialize Firestore
    if (options.enableLogging) {
      console.log('Initializing Firestore...');
    }

    result.firestore = initializeFirestore({
      enablePersistence: options.enablePersistence
    });

    if (!result.firestore) {
      result.errors.push('Failed to initialize Firestore');
    } else if (options.enableLogging) {
      console.log('Firestore initialized successfully');
    }

    // Set overall success status
    result.success = result.app && result.auth && result.firestore;

    if (options.enableLogging) {
      if (result.success) {
        console.log('Firebase services initialized successfully');
      } else {
        console.error('Firebase services initialization failed:', result.errors);
      }
    }

    return result;
  } catch (error) {
    result.errors.push(`Unexpected error: ${error.message}`);
    result.success = false;

    if (options.enableLogging) {
      console.error('Firebase services initialization failed:', error);
    }

    return result;
  }
};

/**
 * Firebase Authentication Service
 */
export const auth = {
  // Core auth functions
  signIn,
  createUser,
  logOut,
  resetPassword,
  updateProfile: updateUserProfile,
  updateEmail: updateUserEmail,
  updatePassword: updateUserPassword,
  getCurrentUser,

  /**
   * Check if user is authenticated
   * @returns {boolean} Whether user is authenticated
   */
  isAuthenticated: () => {
    return !!getCurrentUser();
  },

  /**
   * Get user ID
   * @returns {string|null} User ID or null if not authenticated
   */
  getUserId: () => {
    const user = getCurrentUser();
    return user ? user.uid : null;
  },

  /**
   * Get user email
   * @returns {string|null} User email or null if not authenticated
   */
  getUserEmail: () => {
    const user = getCurrentUser();
    return user ? user.email : null;
  },

  /**
   * Get user display name
   * @returns {string|null} User display name or null if not authenticated
   */
  getUserDisplayName: () => {
    const user = getCurrentUser();
    return user ? user.displayName : null;
  },

  /**
   * Get user photo URL
   * @returns {string|null} User photo URL or null if not authenticated
   */
  getUserPhotoUrl: () => {
    const user = getCurrentUser();
    return user ? user.photoURL : null;
  }
};

/**
 * Firebase Firestore Service
 */
export const firestore = {
  // Core Firestore functions
  createDocument,
  setDocument,
  updateDocument,
  deleteDocument,
  getDocument,
  queryDocuments,
  subscribeToDocument,
  subscribeToQuery,
  createBatch,
  goOffline,
  goOnline,
  getServerTimestamp,
  createTimestamp,

  /**
   * Get all documents in a collection
   * @param {string} collectionPath - Collection path
   * @returns {Promise<Array>} Documents
   */
  getAllDocuments: async (collectionPath) => {
    return queryDocuments(collectionPath);
  },

  /**
   * Get documents by field value
   * @param {string} collectionPath - Collection path
   * @param {string} field - Field name
   * @param {any} value - Field value
   * @returns {Promise<Array>} Documents
   */
  getDocumentsByField: async (collectionPath, field, value) => {
    return queryDocuments(collectionPath, [[field, '==', value]]);
  },

  /**
   * Get documents created by current user
   * @param {string} collectionPath - Collection path
   * @param {string} userIdField - User ID field name
   * @returns {Promise<Array>} Documents
   */
  getUserDocuments: async (collectionPath, userIdField = 'userId') => {
    const userId = auth.getUserId();
    if (!userId) {
      return [];
    }

    return queryDocuments(collectionPath, [[userIdField, '==', userId]]);
  },

  /**
   * Create a document with user ID
   * @param {string} collectionPath - Collection path
   * @param {Object} data - Document data
   * @param {string} userIdField - User ID field name
   * @returns {Promise<string>} Document ID
   */
  createUserDocument: async (collectionPath, data, userIdField = 'userId') => {
    const userId = auth.getUserId();
    if (!userId) {
      throw new Error('User not authenticated');
    }

    const documentData = {
      ...data,
      [userIdField]: userId
    };

    return createDocument(collectionPath, documentData);
  }
};

/**
 * Firebase Service
 */
export const firebase = {
  // Core Firebase functions
  initialize: initializeFirebase,
  getApp: getFirebaseApp,
  getAuth: getFirebaseAuth,
  getFirestore: getFirestoreDb,

  // Firebase services
  auth,
  firestore
};

// Export Firebase service
export default firebase;"}

