    console.log('initErrorTracking: About to call Sentry.init');
    try {
      const initOptions = {
        dsn: config.dsn,
        environment: config.environment,
        release: config.release,
        maxBreadcrumbs: 50,
        debug: config.debug,
        beforeSend: (event) => {
