import { startTransaction } from './errorTracking';
import { info, error as logError, LogCategory } from './logging';
import { safeErrorCapture } from '../atoms/errorUtils';
import { isDevelopment } from '../atoms/envConfig';

/**
 * Transaction types
 */
export const TransactionType = {
  NAVIGATION: 'navigation',
  API_REQUEST: 'api_request',
  UI_RENDER: 'ui_render',
  DATA_OPERATION: 'data_operation',
  BACKGROUND_TASK: 'background_task',
  USER_INTERACTION: 'user_interaction',
  STARTUP: 'startup',
  BETTING: 'betting',
  PAYMENT: 'payment',
  SYNC: 'sync',
};

/**
 * Performance metrics interface
 * @typedef {Object} PerformanceMetrics
 * @property {number} [appStartTime] - App start time
 * @property {number} [timeToInteractive] - Time to interactive
 * @property {number} [firstContentfulPaint] - First contentful paint
 * @property {number} [memoryUsage] - Memory usage
 * @property {number} [cpuUsage] - CPU usage
 * @property {number} [batteryLevel] - Battery level
 * @property {string} [networkType] - Network type
 * @property {boolean} [isLowMemoryDevice] - Whether device has low memory
 * @property {string} [deviceModel] - Device model
 * @property {string} [osVersion] - OS version
 * @property {string} [appVersion] - App version
 */

// Global performance metrics
let performanceMetrics = {};

/**
 * Initialize performance monitoring
 * @param {Object} [options] - Initialization options
 * @param {boolean} [options.trackAppStart] - Whether to track app start time
 * @param {boolean} [options.trackDeviceInfo] - Whether to track device information
 * @returns {boolean} Whether initialization was successful
 */
export const initPerformanceMonitoring = (options = {
  trackAppStart: true,
  trackDeviceInfo: true,
}) => {
  console.log('initPerformanceMonitoring: Starting initialization');
  try {
    // Track app start time
    if (options.trackAppStart) {
      console.log('initPerformanceMonitoring: Setting app start time');
      performanceMetrics.appStartTime = Date.now();
    }

    // Set device information
    if (options.trackDeviceInfo) {
      console.log('initPerformanceMonitoring: Setting device information');
      performanceMetrics.deviceModel = 'Web Browser';
      performanceMetrics.osVersion = navigator.userAgent;
      performanceMetrics.appVersion = process.env.APP_VERSION || '1.0.0';
    }

    console.log('initPerformanceMonitoring: Initialization completed successfully');
    info(LogCategory.PERFORMANCE, 'Performance monitoring initialized successfully');
    return true;
  } catch (initError) {
    console.error('initPerformanceMonitoring: Failed to initialize:', initError);
    logError(LogCategory.PERFORMANCE, 'Failed to initialize performance monitoring', initError);
    safeErrorCapture(initError);
    return false;
  }
};

/**
 * Start a performance transaction
 * @param {string} name - Transaction name
 * @param {string} type - Transaction type
 * @param {Object} [data] - Additional data
 * @returns {Object} Transaction object
 */
export const startPerformanceTransaction = (
  name,
  type,
  data
) => {
  try {
    // Create a transaction using Sentry
    const transaction = startTransaction(name, type);

    // Add data as tags
    if (data && transaction) {
      Object.entries(data).forEach(([key, value]) => {
        if (typeof value === 'string' || typeof value === 'number' || typeof value === 'boolean') {
          transaction.setTag(key, String(value));
        }
      });
    }

    // Return transaction with additional methods
    return {
      ...transaction,
      addData: (key, value) => {
        if (transaction && typeof transaction.setTag === 'function') {
          transaction.setTag(key, String(value));
        }
      },
      markPoint: (pointName) => {
        if (isDevelopment) {
          console.log(`Performance mark: ${pointName} in transaction ${name}`);
        }
        // In a real implementation, this would mark a point in the transaction
      },
    };
  } catch (error) {
    console.error('Failed to start transaction:', error);
    logError(LogCategory.PERFORMANCE, 'Failed to start transaction', error);
    safeErrorCapture(error);

    // Return a dummy transaction
    return {
      finish: () => {},
      addData: () => {},
      markPoint: () => {},
      setTag: () => {},
      setData: () => {},
    };
  }
};

/**
 * Track a navigation event
 * @param {string} routeName - Route name
 * @param {string} [previousRoute] - Previous route
 */
export const trackNavigation = (routeName, previousRoute) => {
  try {
    const transaction = startPerformanceTransaction(
      `Navigation: ${routeName}`,
      TransactionType.NAVIGATION,
      { previousRoute }
    );

    // Log navigation
    info(LogCategory.NAVIGATION, `Navigated to ${routeName}`, { previousRoute });

    // Finish the transaction after a delay to capture render time
    setTimeout(() => {
      transaction?.finish();
    }, 1000);
  } catch (error) {
    console.error('Failed to track navigation:', error);
    logError(LogCategory.PERFORMANCE, 'Failed to track navigation', error);
    safeErrorCapture(error);
  }
};

/**
 * Track an API request
 * @param {string} url - Request URL
 * @param {string} method - HTTP method
 * @param {number} status - HTTP status code
 * @param {number} duration - Request duration in ms
 */
export const trackApiRequest = (
  url,
  method,
  status,
  duration
) => {
  try {
    const transaction = startPerformanceTransaction(
      `API: ${method} ${getUrlPath(url)}`,
      TransactionType.API_REQUEST,
      { url, method, status, duration }
    );

    // Log API request
    if (status >= 400) {
      logError(
        LogCategory.API,
        `API request failed: ${method} ${getUrlPath(url)} (${status})`,
        new Error(`API request failed with status ${status}`),
        { url, method, status, duration }
      );
    } else {
      info(
        LogCategory.API,
        `API request: ${method} ${getUrlPath(url)} (${status})`,
        { url, method, status, duration }
      );
    }

    // Finish the transaction immediately
    transaction?.finish();
  } catch (error) {
    console.error('Failed to track API request:', error);
    logError(LogCategory.PERFORMANCE, 'Failed to track API request', error);
    safeErrorCapture(error);
  }
};

/**
 * Track a UI render
 * @param {string} componentName - Component name
 * @param {number} duration - Render duration in ms
 */
export const trackUiRender = (componentName, duration) => {
  try {
    const transaction = startPerformanceTransaction(
      `Render: ${componentName}`,
      TransactionType.UI_RENDER,
      { componentName, duration }
    );

    // Log UI render if it's slow
    if (duration > 100) {
      info(
        LogCategory.PERFORMANCE,
        `Slow render: ${componentName} (${duration}ms)`,
        { componentName, duration }
      );
    }

    // Finish the transaction immediately
    transaction?.finish();
  } catch (error) {
    console.error('Failed to track UI render:', error);
    logError(LogCategory.PERFORMANCE, 'Failed to track UI render', error);
    safeErrorCapture(error);
  }
};

/**
 * Track a data operation
 * @param {string} operationName - Operation name
 * @param {number} duration - Operation duration in ms
 * @param {Object} [data] - Additional data
 */
export const trackDataOperation = (
  operationName,
  duration,
  data
) => {
  try {
    const transaction = startPerformanceTransaction(
      `Data: ${operationName}`,
      TransactionType.DATA_OPERATION,
      { ...data, duration }
    );

    // Log data operation if it's slow
    if (duration > 500) {
      info(
        LogCategory.PERFORMANCE,
        `Slow data operation: ${operationName} (${duration}ms)`,
        { operationName, duration, ...data }
      );
    }

    // Finish the transaction immediately
    transaction?.finish();
  } catch (error) {
    console.error('Failed to track data operation:', error);
    logError(LogCategory.PERFORMANCE, 'Failed to track data operation', error);
    safeErrorCapture(error);
  }
};

/**
 * Track a user interaction
 * @param {string} interactionName - Interaction name
 * @param {number} duration - Interaction duration in ms
 * @param {Object} [data] - Additional data
 */
export const trackUserInteraction = (
  interactionName,
  duration,
  data
) => {
  try {
    const transaction = startPerformanceTransaction(
      `Interaction: ${interactionName}`,
      TransactionType.USER_INTERACTION,
      { ...data, duration }
    );

    // Log user interaction if it's slow
    if (duration > 300) {
      info(
        LogCategory.PERFORMANCE,
        `Slow user interaction: ${interactionName} (${duration}ms)`,
        { interactionName, duration, ...data }
      );
    }

    // Finish the transaction immediately
    transaction?.finish();
  } catch (error) {
    console.error('Failed to track user interaction:', error);
    logError(LogCategory.PERFORMANCE, 'Failed to track user interaction', error);
    safeErrorCapture(error);
  }
};

/**
 * Track app startup
 * @param {number} duration - Startup duration in ms
 * @param {Object} [data] - Additional data
 */
export const trackAppStartup = (duration, data) => {
  try {
    const transaction = startPerformanceTransaction(
      'App Startup',
      TransactionType.STARTUP,
      { ...data, duration }
    );

    // Update performance metrics
    updatePerformanceMetrics({
      timeToInteractive: duration,
    });

    // Log app startup
    info(
      LogCategory.PERFORMANCE,
      `App startup completed in ${duration}ms`,
      { duration, ...data }
    );

    // Finish the transaction immediately
    transaction?.finish();
  } catch (error) {
    console.error('Failed to track app startup:', error);
    logError(LogCategory.PERFORMANCE, 'Failed to track app startup', error);
    safeErrorCapture(error);
  }
};

/**
 * Update performance metrics
 * @param {PerformanceMetrics} metrics - Metrics to update
 */
export const updatePerformanceMetrics = (metrics) => {
  try {
    performanceMetrics = {
      ...performanceMetrics,
      ...metrics,
    };
  } catch (error) {
    console.error('Failed to update performance metrics:', error);
    logError(LogCategory.PERFORMANCE, 'Failed to update performance metrics', error);
    safeErrorCapture(error);
  }
};

/**
 * Get performance metrics
 * @returns {PerformanceMetrics} Current performance metrics
 */
export const getPerformanceMetrics = () => {
  return { ...performanceMetrics };
};

/**
 * Get the path from a URL
 * @param {string} url - URL
 * @returns {string} URL path
 */
const getUrlPath = (url) => {
  try {
    const urlObj = new URL(url);
    return urlObj.pathname;
  } catch (error) {
    // If URL parsing fails, return the original URL
    return url;
  }
};

/**
 * Create a performance timer
 * @param {string} name - Timer name
 * @param {string} type - Transaction type
 * @param {Object} [data] - Additional data
 * @returns {Object} Timer object
 */
export const createPerformanceTimer = (name, type, data) => {
  const startTime = Date.now();
  let transaction = null;

  // Start transaction if type is provided
  if (type) {
    transaction = startPerformanceTransaction(name, type, data);
  }

  return {
    /**
     * Stop the timer and record the duration
     * @param {Object} [additionalData] - Additional data to include
     * @returns {number} Duration in ms
     */
    stop: (additionalData) => {
      const endTime = Date.now();
      const duration = endTime - startTime;

      // Add duration to transaction
      if (transaction) {
        transaction.addData('duration', duration);

        // Add additional data
        if (additionalData) {
          Object.entries(additionalData).forEach(([key, value]) => {
            transaction.addData(key, value);
          });
        }

        // Finish transaction
        transaction.finish();
      }

      return duration;
    },

    /**
     * Mark a point in the timer
     * @param {string} pointName - Point name
     */
    mark: (pointName) => {
      if (transaction) {
        transaction.markPoint(pointName);
      }
    },
  };
};"}

{"tool":"newFileCreated","path":"atomic/organisms/monitoringService.js","isOutsideWorkspace":false,"content":"/**
 * Monitoring Service Organism
 * 
 * Integrates error tracking, logging, and performance monitoring
 * into a cohesive monitoring solution.
 */

import {
  initErrorTracking,
  captureException,
  captureMessage,
  addBreadcrumb,
  setUser,
  setTag,
  setTags,
  setExtra,
  setExtras,
  startTransaction,
  flush as flushSentry,
  close as closeSentry
} from '../molecules/errorTracking';

import {
  initLogging,
  LogLevel,
  LogCategory,
  log,
  trace,
  debug,
  info,
  warn,
  error,
  fatal,
  createLogger,
  setMinLogLevel,
  setGlobalMinLogLevel,
  enableConsoleLogging,
  enableSentryLogging,
  enableRemoteLogging,
  manualFlushLogs
} from '../molecules/logging';

import {
  initPerformanceMonitoring,
  TransactionType,
  trackNavigation,
  trackApiRequest,
  trackUiRender,
  trackDataOperation,
  trackUserInteraction,
  trackAppStartup,
  updatePerformanceMetrics,
  getPerformanceMetrics,
  createPerformanceTimer
} from '../molecules/performance';

import {
  safeErrorCapture,
  formatError,
  createError,
  parseError,
  getErrorCode,
  getUserFriendlyMessage,
  isNetworkError,
  isAuthError,
  isPermissionError
} from '../atoms/errorUtils';

/**
 * Monitoring service initialization options
 * @typedef {Object} MonitoringOptions
 * @property {Object} [errorTracking] - Error tracking options
 * @property {string} [errorTracking.dsn] - Sentry DSN
 * @property {string} [errorTracking.environment] - Environment
 * @property {string} [errorTracking.release] - Release version
 * @property {number} [errorTracking.tracesSampleRate] - Traces sample rate
 * @property {boolean} [errorTracking.debug] - Whether to enable debug mode
 * @property {Object} [logging] - Logging options
 * @property {boolean} [logging.enableConsole] - Whether to enable console logging
 * @property {boolean} [logging.enableSentry] - Whether to enable Sentry logging
 * @property {boolean} [logging.enableRemote] - Whether to enable remote logging
 * @property {number} [logging.flushInterval] - Log flush interval in ms
 * @property {Object} [performance] - Performance monitoring options
 * @property {boolean} [performance.trackAppStart] - Whether to track app start time
 * @property {boolean} [performance.trackDeviceInfo] - Whether to track device information
 */

/**
 * Initialize monitoring services
 * @param {MonitoringOptions} [options] - Initialization options
 * @returns {Object} Initialization result
 */
export const initMonitoring = (options = {}) => {
  console.log('Initializing monitoring services...');

  const result = {
    success: false,
    errorTracking: false,
    logging: false,
    performance: false,
    errors: []
  };

  try {
    // Initialize logging first
    result.logging = initLogging(options.logging);

    if (result.logging) {
      info(LogCategory.APP, 'Logging service initialized');
    } else {
      console.error('Failed to initialize logging service');
      result.errors.push('Failed to initialize logging service');
    }

    // Initialize error tracking
    try {
      result.errorTracking = initErrorTracking(options.errorTracking);

      if (result.errorTracking) {
        info(LogCategory.APP, 'Error tracking service initialized');
      } else {
        console.error('Failed to initialize error tracking service');
        result.errors.push('Failed to initialize error tracking service');
      }
    } catch (err) {
      console.error('Error tracking initialization failed:', err);
      result.errors.push(`Error tracking initialization failed: ${err.message}`);
    }

    // Initialize performance monitoring
    try {
      result.performance = initPerformanceMonitoring(options.performance);

      if (result.performance) {
        info(LogCategory.APP, 'Performance monitoring service initialized');
      } else {
        console.error('Failed to initialize performance monitoring service');
        result.errors.push('Failed to initialize performance monitoring service');
      }
    } catch (err) {
      console.error('Performance monitoring initialization failed:', err);
      result.errors.push(`Performance monitoring initialization failed: ${err.message}`);
    }

    // Set overall success status
    result.success = result.logging && result.errorTracking && result.performance;

    if (result.success) {
      info(LogCategory.APP, 'All monitoring services initialized successfully');
    } else {
      warn(LogCategory.APP, 'Some monitoring services failed to initialize', { result });
    }

    return result;
  } catch (error) {
    console.error('Unexpected error during monitoring initialization:', error);
    result.errors.push(`Unexpected error: ${error.message}`);
    result.success = false;
    return result;
  }
};

/**
 * Shutdown monitoring services
 * @returns {Promise<boolean>} Whether shutdown was successful
 */
export const shutdownMonitoring = async () => {
  try {
    info(LogCategory.APP, 'Shutting down monitoring services');

    // Flush logs
    await manualFlushLogs();

    // Close Sentry
    await closeSentry();

    info(LogCategory.APP, 'Monitoring services shut down successfully');
    return true;
  } catch (error) {
    console.error('Failed to shut down monitoring services:', error);
    return false;
  }
};

/**
 * Error service
 */
export const errorService = {
  // Core error functions
  captureException,
  captureMessage,
  addBreadcrumb,
  setUser,
  setTag,
  setTags,
  setExtra,
  setExtras,

  // Error utilities
  safeErrorCapture,
  formatError,
  createError,
  parseError,
  getErrorCode,
  getUserFriendlyMessage,
  isNetworkError,
  isAuthError,
  isPermissionError,

  /**
   * Handle error with appropriate logging and user feedback
   * @param {Error} error - Error to handle
   * @param {Object} [options] - Options
   * @param {string} [options.category] - Log category
   * @param {string} [options.context] - Error context
   * @param {boolean} [options.silent] - Whether to handle silently
   * @returns {string} User-friendly error message
   */
  handleError: (error, options = {}) => {
    const category = options.category || LogCategory.APP;
    const context = options.context || 'General error';
    const silent = options.silent || false;

    // Log error
    if (!silent) {
      this.error(category, `${context}: ${error.message}`, error);
    }

    // Capture in Sentry
    captureException(error, { tags: { category, context } });

    // Return user-friendly message
    return getUserFriendlyMessage(error);
  },

  /**
   * Create and throw an error
   * @param {string} message - Error message
   * @param {string} [code] - Error code
   * @param {Object} [details] - Error details
   * @throws {Error} Created error
   */
  throwError: (message, code, details) => {
    throw createError(message, code, details);
  }
};

/**
 * Logging service
 */
export const loggingService = {
  // Log levels
  LogLevel,

  // Log categories
  LogCategory,

  // Core logging functions
  log,
  trace,
  debug,
  info,
  warn,
  error,
  fatal,

  // Advanced logging functions
  createLogger,
  setMinLogLevel,
  setGlobalMinLogLevel,
  enableConsoleLogging,
  enableSentryLogging,
  enableRemoteLogging,
  flushLogs: manualFlushLogs,

  /**
   * Create a scoped logger with predefined tags
   * @param {string} category - Log category
   * @param {Object} [tags] - Default tags
   * @returns {Object} Scoped logger
   */
  createScopedLogger: (category, tags = {}) => {
    const logger = createLogger(category);

    return {
      trace: (message, data) => logger.trace(message, data, tags),
      debug: (message, data) => logger.debug(message, data, tags),
      info: (message, data) => logger.info(message, data, tags),
      warn: (message, data) => logger.warn(message, data, tags),
      error: (message, error, data) => logger.error(message, error, data, tags),
      fatal: (message, error, data) => logger.fatal(message, error, data, tags),
    };
  }
};

/**
 * Performance service
 */
export const performanceService = {
  // Transaction types
  TransactionType,

  // Core performance functions
  trackNavigation,
  trackApiRequest,
  trackUiRender,
  trackDataOperation,
  trackUserInteraction,
  trackAppStartup,
  updatePerformanceMetrics,
  getPerformanceMetrics,
  createPerformanceTimer,

  /**
   * Measure function execution time
   * @param {Function} fn - Function to measure
   * @param {string} name - Measurement name
   * @param {string} type - Transaction type
   * @param {Object} [data] - Additional data
   * @returns {any} Function result
   */
  measure: (fn, name, type, data) => {
    const timer = createPerformanceTimer(name, type, data);

    try {
      const result = fn();

      // Handle promises
      if (result instanceof Promise) {
        return result.then(value => {
          timer.stop();
          return value;
        }).catch(err => {
          timer.stop({ error: true });
          throw err;
        });
      }

      timer.stop();
      return result;
    } catch (error) {
      timer.stop({ error: true });
      throw error;
    }
  },

  /**
   * Create a performance-tracked version of a function
   * @param {Function} fn - Function to track
   * @param {string} name - Function name
   * @param {string} type - Transaction type
   * @param {Object} [data] - Additional data
   * @returns {Function} Tracked function
   */
  trackFunction: (fn, name, type, data) => {
    return (...args) => {
      return performanceService.measure(() => fn(...args), name, type, data);
    };
  }
};

/**
 * Monitoring service
 */
export const monitoring = {
  // Core monitoring functions
  initialize: initMonitoring,
  shutdown: shutdownMonitoring,

  // Services
  error: errorService,
  logging: loggingService,
  performance: performanceService,

  /**
   * Get monitoring status
   * @returns {Object} Monitoring status
   */
  getStatus: () => {
    return {
      timestamp: new Date().toISOString(),
      performanceMetrics: getPerformanceMetrics(),
    };
  }
};

// Export monitoring service
export default monitoring;"}

{"tool":"newFileCreated","path":"atomic-architecture-summary.md","isOutsideWorkspace":false,"content":"# AI Sports Edge - Atomic Architecture Implementation

## Overview

We've successfully refactored the AI Sports Edge codebase into an atomic architecture, creating a more modular, maintainable, and scalable structure. This architecture follows atomic design principles, organizing code into atoms, molecules, organisms, templates, and pages.

## Implemented Modules

### 1. Environment Module
- **Purpose**: Handle environment configuration and validation
- **Components**:
  - `atoms/envConfig.js`: Environment variable access
  - `atoms/serviceConfig.js`: Service-specific configurations
  - `atoms/envValidator.js`: Environment validation utilities
  - `molecules/environmentValidator.js`: Complete environment validation
  - `organisms/environmentBootstrap.js`: Environment initialization

### 2. Firebase Module
- **Purpose**: Provide Firebase functionality
- **Components**:
  - `atoms/firebaseApp.js`: Firebase app initialization
  - `molecules/firebaseAuth.js`: Authentication functionality
  - `molecules/firebaseFirestore.js`: Database operations
  - `organisms/firebaseService.js`: Integrated Firebase service

### 3. Theme Module
- **Purpose**: Manage application theming
- **Components**:
  - `atoms/themeColors.js`: Color definitions
  - `atoms/themeTokens.js`: Design tokens (spacing, typography, etc.)
  - `molecules/themeContext.js`: Theme context and utilities
  - `organisms/themeProvider.js`: Theme state management

### 4. Monitoring Module
- **Purpose**: Handle error tracking, logging, and performance monitoring
- **Components**:
  - `atoms/errorUtils.js`: Error handling utilities
  - `molecules/errorTracking.js`: Error tracking with Sentry
  - `molecules/logging.js`: Structured logging
  - `molecules/performance.js`: Performance monitoring
  - `organisms/monitoringService.js`: Integrated monitoring service

## Benefits of Atomic Architecture

1. **Modularity**: Each component has a single responsibility
2. **Reusability**: Components can be reused across the application
3. **Maintainability**: Changes to one component don't affect others
4. **Scalability**: New features can be built using existing components
5. **Testability**: Components can be tested in isolation
6. **Consistency**: Standardized patterns across the codebase
7. **Documentation**: Self-documenting code structure

## Usage Examples

### Initializing the Application

```javascript
import React from 'react';
import { initMonitoring } from 'atomic/organisms/monitoringService';
import { bootstrapEnvironment } from 'atomic/organisms/environmentBootstrap';
import firebase from 'atomic/organisms/firebaseService';
import ThemeProvider from 'atomic/organisms/themeProvider';
import App from './App';

// Initialize environment
const envResult = bootstrapEnvironment();

// Initialize monitoring
const monitoringResult = initMonitoring();

// Initialize Firebase
const firebaseResult = firebase.initialize();

// Render app with providers
const Root = () => (
  <ThemeProvider>
    <App />
  </ThemeProvider>
);

export default Root;
```

### Using Firebase Services

```javascript
import firebase from 'atomic/organisms/firebaseService';

// Authentication
const signIn = async (email, password) => {
  try {
    const userCredential = await firebase.auth.signIn(email, password);
    return userCredential.user;
  } catch (error) {
    console.error('Sign in error:', error);
    throw error;
  }
};

// Firestore
const getUserData = async (userId) => {
  try {
    const userData = await firebase.firestore.getDocument('users', userId);
    return userData;
  } catch (error) {
    console.error('Get user data error:', error);
    throw error;
  }
};
```

### Using Theme

```javascript
import React from 'react';
import { useTheme } from 'atomic/molecules/themeContext';
import { View, Text, TouchableOpacity } from 'react-native';

const MyComponent = () => {
  const { theme, colors, toggleTheme } = useTheme();

  return (
    <View style={{ backgroundColor: colors.background }}>
      <Text style={{ color: colors.text }}>
        Current theme: {theme}
      </Text>
      <TouchableOpacity 
        onPress={toggleTheme}
        style={{ backgroundColor: colors.primary }}
      >
        <Text style={{ color: colors.background }}>
          Toggle Theme
        </Text>
      </TouchableOpacity>
    </View>
  );
};
```

### Using Monitoring

```javascript
import monitoring from 'atomic/organisms/monitoringService';

// Logging
const logger = monitoring.logging.createLogger(monitoring.logging.LogCategory.APP);
logger.info('Application started');

// Error handling
try {
  // Some code that might throw an error
} catch (error) {
  monitoring.error.captureException(error);
  const userMessage = monitoring.error.getUserFriendlyMessage(error);
  // Show user message
}

// Performance monitoring
const timer = monitoring.performance.createPerformanceTimer(
  'DataLoad', 
  monitoring.performance.TransactionType.DATA_OPERATION
);
// Perform data loading
const data = await loadData();
const duration = timer.stop();
console.log(`Data loaded in ${duration}ms`);
