{"tool":"newFileCreated","path":"examples/ProfileScreen.js","isOutsideWorkspace":false,"content":"/**
 * Profile Screen Example
 * 
 * This example demonstrates how to use the atomic architecture
 * components in a real-world scenario.
 */

import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, ActivityIndicator } from 'react-native';

// Import atomic components
import { useTheme } from '../atomic/molecules/themeContext';
import firebase from '../atomic/organisms/firebaseService';
import monitoring from '../atomic/organisms/monitoringService';

// Create loggers
const logger = monitoring.logging.createLogger(monitoring.logging.LogCategory.USER);
const perfLogger = monitoring.logging.createLogger(monitoring.logging.LogCategory.PERFORMANCE);

const ProfileScreen = ({ navigation, route }) => {
  // Get theme from context
  const { colors, theme, toggleTheme } = useTheme();

  // State
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Get user data on mount
  useEffect(() => {
    const fetchUserData = async () => {
      // Start performance timer
      const timer = monitoring.performance.createPerformanceTimer(
        'FetchUserData',
        monitoring.performance.TransactionType.DATA_OPERATION
      );

      try {
        // Get current user
        const currentUser = firebase.auth.getCurrentUser();

        if (!currentUser) {
          logger.warn('No user logged in, redirecting to login');
          navigation.replace('Login');
          return;
        }

        // Log user access
        logger.info('User accessing profile', { userId: currentUser.uid });

        // Get user data from Firestore
        const userData = await firebase.firestore.getDocument('users', currentUser.uid);

        if (!userData) {
          // Create user document if it doesn't exist
          const newUserData = {
            displayName: currentUser.displayName || 'New User',
            email: currentUser.email,
            createdAt: firebase.firestore.getServerTimestamp(),
            theme: theme,
          };

          await firebase.firestore.setDocument('users', currentUser.uid, newUserData);
          setUser(newUserData);
        } else {
          setUser(userData);
        }

        // Stop timer and log performance
        const duration = timer.stop();
        perfLogger.info(`User data loaded in ${duration}ms`, { duration });
      } catch (err) {
        // Handle error
        timer.stop({ error: true });

        // Log error
        logger.error('Failed to load user data', err);

        // Capture exception
        monitoring.error.captureException(err);

        // Set user-friendly error message
        setError(monitoring.error.getUserFriendlyMessage(err));
      } finally {
        setLoading(false);
      }
    };

    fetchUserData();

    // Track screen view
    monitoring.performance.trackNavigation('Profile', route.params?.previousScreen);

    // Clean up
    return () => {
      logger.debug('Profile screen unmounted');
    };
  }, []);

  // Update user theme preference
  const handleToggleTheme = async () => {
    try {
      // Toggle theme
      toggleTheme();

      // Update user preference in Firestore
      if (user) {
        await firebase.firestore.updateDocument('users', user.id, {
          theme: theme === 'light' ? 'dark' : 'light',
        });

        logger.info('User theme preference updated', { theme: theme === 'light' ? 'dark' : 'light' });
      }
    } catch (err) {
      logger.error('Failed to update theme preference', err);
      monitoring.error.captureException(err);
    }
  };

  // Handle sign out
  const handleSignOut = async () => {
    try {
      await firebase.auth.logOut();
      logger.info('User signed out');
      navigation.replace('Login');
    } catch (err) {
      logger.error('Failed to sign out', err);
      monitoring.error.captureException(err);
    }
  };

  // Render loading state
  if (loading) {
    return (
      <View style={[styles.container, { backgroundColor: colors.background }]}>
        <ActivityIndicator size="large" color={colors.primary} />
        <Text style={[styles.text, { color: colors.text }]}>Loading profile...</Text>
      </View>
    );
  }

  // Render error state
  if (error) {
    return (
      <View style={[styles.container, { backgroundColor: colors.background }]}>
        <Text style={[styles.errorText, { color: colors.error }]}>{error}</Text>
        <TouchableOpacity
          style={[styles.button, { backgroundColor: colors.primary }]}
          onPress={() => navigation.goBack()}
        >
          <Text style={[styles.buttonText, { color: colors.background }]}>Go Back</Text>
        </TouchableOpacity>
      </View>
    );
  }

  // Render profile
  return (
    <View style={[styles.container, { backgroundColor: colors.background }]}>
      <Text style={[styles.title, { color: colors.text }]}>Profile</Text>

      {user && (
        <View style={styles.profileContainer}>
          <Text style={[styles.name, { color: colors.text }]}>{user.displayName}</Text>
          <Text style={[styles.email, { color: colors.textSecondary }]}>{user.email}</Text>

          <View style={styles.statsContainer}>
            <Text style={[styles.statsLabel, { color: colors.textSecondary }]}>Member since:</Text>
            <Text style={[styles.statsValue, { color: colors.text }]}>
              {user.createdAt ? new Date(user.createdAt.toDate()).toLocaleDateString() : 'N/A'}
            </Text>
          </View>
        </View>
      )}

      <View style={styles.buttonContainer}>
        <TouchableOpacity
          style={[styles.button, { backgroundColor: colors.primary }]}
          onPress={handleToggleTheme}
        >
          <Text style={[styles.buttonText, { color: colors.background }]}>
            Switch to {theme === 'light' ? 'Dark' : 'Light'} Mode
          </Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={[styles.button, { backgroundColor: colors.error }]}
          onPress={handleSignOut}
        >
          <Text style={[styles.buttonText, { color: colors.background }]}>Sign Out</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
    alignItems: 'center',
    justifyContent: 'center',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  profileContainer: {
    width: '100%',
    padding: 20,
    borderRadius: 10,
    marginBottom: 20,
  },
  name: {
    fontSize: 22,
    fontWeight: 'bold',
    marginBottom: 5,
  },
  email: {
    fontSize: 16,
    marginBottom: 15,
  },
  statsContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginBottom: 5,
  },
  statsLabel: {
    fontSize: 14,
  },
  statsValue: {
    fontSize: 14,
    fontWeight: 'bold',
  },
  buttonContainer: {
    width: '100%',
    marginTop: 20,
  },
  button: {
    padding: 15,
    borderRadius: 5,
    alignItems: 'center',
    marginBottom: 10,
  },
  buttonText: {
    fontSize: 16,
    fontWeight: 'bold',
  },
  text: {
    fontSize: 16,
    marginTop: 10,
  },
  errorText: {
    fontSize: 16,
    marginBottom: 20,
    textAlign: 'center',
  },
});

export default ProfileScreen;"}

{"tool":"newFileCreated","path":"examples/AppInitialization.js","isOutsideWorkspace":false,"content":"/**
 * App Initialization Example
 * 
 * This example demonstrates how to initialize the application
 * using the atomic architecture components.
 */

import React, { useState, useEffect } from 'react';
import { View, Text, ActivityIndicator, StyleSheet } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';

// Import atomic components
import { bootstrapEnvironment } from '../atomic/organisms/environmentBootstrap';
import firebase from '../atomic/organisms/firebaseService';
import monitoring from '../atomic/organisms/monitoringService';
import ThemeProvider from '../atomic/organisms/themeProvider';
import { useTheme } from '../atomic/molecules/themeContext';

// Import navigation
import AppNavigator from '../navigation/AppNavigator';

// App initialization component
const AppInitialization = () => {
  // State for initialization status
  const [initStatus, setInitStatus] = useState({
    environment: false,
    monitoring: false,
    firebase: false,
    complete: false,
    error: null
  });

  // Initialize app on mount
  useEffect(() => {
    const initializeApp = async () => {
      try {
        // Initialize environment first
        console.log('Initializing environment...');
        const envResult = bootstrapEnvironment();
        setInitStatus(prev => ({ ...prev, environment: envResult.success }));

        if (!envResult.success) {
          throw new Error('Environment initialization failed');
        }

        // Initialize monitoring services
        console.log('Initializing monitoring services...');
        const monitoringResult = monitoring.initialize();
        setInitStatus(prev => ({ ...prev, monitoring: monitoringResult.success }));

        // Log initialization progress
        if (monitoringResult.success) {
          monitoring.logging.info(
            monitoring.logging.LogCategory.APP,
            'Monitoring services initialized'
          );
        } else {
          console.warn('Monitoring initialization issues:', monitoringResult.errors);
        }

        // Initialize Firebase
        console.log('Initializing Firebase...');
        const firebaseResult = firebase.initialize();
        setInitStatus(prev => ({ ...prev, firebase: firebaseResult.success }));

        // Log Firebase initialization
        if (monitoringResult.success) {
          monitoring.logging.info(
            monitoring.logging.LogCategory.APP,
            'Firebase services initialized',
            { success: firebaseResult.success }
          );
        }

        // Mark initialization as complete
        setInitStatus(prev => ({ ...prev, complete: true }));

        // Track app startup performance
        if (monitoringResult.success) {
          monitoring.performance.trackAppStartup(
            Date.now() - performance.timing.navigationStart,
            {
              environment: envResult.success,
              monitoring: monitoringResult.success,
              firebase: firebaseResult.success
            }
          );
        }
      } catch (error) {
        console.error('Initialization error:', error);

        // Log error if monitoring is available
        try {
          monitoring.error.captureException(error);
          monitoring.logging.error(
            monitoring.logging.LogCategory.APP,
            'App initialization failed',
            error
          );
        } catch (loggingError) {
          // Fallback to console if monitoring failed
          console.error('Logging error:', loggingError);
        }

        // Update state with error
        setInitStatus(prev => ({ 
          ...prev, 
          complete: true, 
          error: error.message 
        }));
      }
    };

    initializeApp();
  }, []);

  // If initialization is not complete, show loading screen
  if (!initStatus.complete) {
    return <InitializationLoadingScreen status={initStatus} />;
  }

  // If there was an error, show error screen
  if (initStatus.error) {
    return <InitializationErrorScreen error={initStatus.error} />;
  }

  // Initialization complete, render app
  return (
    <ThemeProvider>
      <AppWithTheme 
        environmentReady={initStatus.environment}
        monitoringReady={initStatus.monitoring}
        firebaseReady={initStatus.firebase}
      />
    </ThemeProvider>
  );
};

// App with theme component
const AppWithTheme = ({ environmentReady, monitoringReady, firebaseReady }) => {
  // Get theme from context
  const { navigationTheme } = useTheme();

  // Log app render if monitoring is available
  useEffect(() => {
    if (monitoringReady) {
      monitoring.logging.info(
        monitoring.logging.LogCategory.APP,
        'App rendered',
        { environmentReady, monitoringReady, firebaseReady }
      );
    }
  }, [environmentReady, monitoringReady, firebaseReady]);

  return (
    <NavigationContainer 
      theme={navigationTheme}
      onReady={() => {
        if (monitoringReady) {
          monitoring.logging.info(
            monitoring.logging.LogCategory.NAVIGATION,
            'Navigation container ready'
          );
        }
      }}
      onStateChange={(state) => {
        if (monitoringReady && state) {
          const currentRoute = state?.routes[state.index]?.name;
          if (currentRoute) {
            monitoring.logging.info(
              monitoring.logging.LogCategory.NAVIGATION,
              `Navigated to ${currentRoute}`
            );

            monitoring.performance.trackNavigation(currentRoute);
          }
        }
      }}
    >
      <AppNavigator />
    </NavigationContainer>
  );
};

// Loading screen component
const InitializationLoadingScreen = ({ status }) => {
  return (
    <View style={styles.container}>
      <ActivityIndicator size="large" color="#0066ff" />
      <Text style={styles.title}>AI Sports Edge</Text>
      <Text style={styles.subtitle}>Initializing...</Text>

      <View style={styles.statusContainer}>
        <Text style={styles.statusText}>
          Environment: {status.environment ? 'âœ…' : 'â³'}
        </Text>
        <Text style={styles.statusText}>
          Monitoring: {status.monitoring ? 'âœ…' : 'â³'}
        </Text>
        <Text style={styles.statusText}>
          Firebase: {status.firebase ? 'âœ…' : 'â³'}
        </Text>
      </View>
    </View>
  );
};

// Error screen component
const InitializationErrorScreen = ({ error }) => {
  return (
    <View style={styles.container}>
      <Text style={styles.title}>Initialization Error</Text>
      <Text style={styles.errorText}>{error}</Text>
      <Text style={styles.helpText}>
        Please restart the app. If the problem persists, contact support.
      </Text>
    </View>
  );
};

// Styles
const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#f8f9fa',
    padding: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginTop: 20,
    marginBottom: 10,
    color: '#212529',
  },
  subtitle: {
    fontSize: 18,
    marginBottom: 20,
    color: '#495057',
  },
  statusContainer: {
    marginTop: 30,
    alignItems: 'flex-start',
    width: '80%',
  },
  statusText: {
    fontSize: 16,
    marginBottom: 10,
    color: '#495057',
  },
  errorText: {
    fontSize: 16,
    color: '#dc3545',
    marginVertical: 20,
    textAlign: 'center',
  },
  helpText: {
    fontSize: 14,
    color: '#6c757d',
    textAlign: 'center',
  },
});

export default AppInitialization;"}

{"tool":"newFileCreated","path":"scripts/update-imports.js","isOutsideWorkspace":false,"content":"#!/usr/bin/env node

/**
 * Import Update Script
 * 
 * This script helps update imports from the old module structure to the new atomic structure.
 * It scans files for old import patterns and suggests replacements.
 */

const fs = require('fs');
const path = require('path');
const { execSync } = require('child_process');

// Configuration
const ROOT_DIR = path.join(__dirname, '..');
const SCAN_DIRS = ['src', 'components', 'screens', 'navigation'];
const EXCLUDE_DIRS = ['node_modules', 'build', 'dist', 'atomic'];

// Import mapping from old to new
const IMPORT_MAPPING = {
  // Environment module
  '../modules/environment/envConfig': '../atomic/atoms/envConfig',
  '../modules/environment/envCheck': '../atomic/molecules/environmentValidator',
  '../modules/environment/index': '../atomic/organisms/environmentBootstrap',

  // Firebase module
  '../modules/firebase/firebaseConfig': '../atomic/atoms/firebaseApp',
  '../modules/firebase/firebaseAuth': '../atomic/molecules/firebaseAuth',
  '../modules/firebase/firebaseFirestore': '../atomic/molecules/firebaseFirestore',
  '../modules/firebase/index': '../atomic/organisms/firebaseService',

  // Theme module
  '../modules/theme/themeConfig': '../atomic/atoms/themeColors',
  '../modules/theme/ThemeContext': '../atomic/molecules/themeContext',
  '../modules/theme/ThemeProvider': '../atomic/organisms/themeProvider',
  '../modules/theme/index': '../atomic/organisms/themeProvider',

  // Monitoring module
  '../modules/monitoring/errorUtils': '../atomic/atoms/errorUtils',
  '../modules/monitoring/errorTracking': '../atomic/molecules/errorTracking',
  '../modules/monitoring/index': '../atomic/organisms/monitoringService',
};

// Function to scan a file for imports
function scanFile(filePath) {
  try {
    const content = fs.readFileSync(filePath, 'utf8');
    const importRegex = /import\s+(?:{[^}]*}|\*\s+as\s+[^;]*|[^;]*)\s+from\s+['"]([^'"]*)['"]/g;

    let match;
    const matches = [];

    while ((match = importRegex.exec(content)) !== null) {
      const importPath = match[1];

      // Check if this import path needs to be updated
      for (const [oldPath, newPath] of Object.entries(IMPORT_MAPPING)) {
        if (importPath === oldPath || importPath.includes(oldPath)) {
          matches.push({
            file: filePath,
            line: content.substring(0, match.index).split('\n').length,
            oldImport: match[0],
            newImport: match[0].replace(oldPath, newPath),
          });
          break;
        }
      }
    }

    return matches;
  } catch (error) {
    console.error(`Error scanning file ${filePath}:`, error.message);
    return [];
  }
}

// Function to scan directories recursively
function scanDirectory(dir, results = []) {
  const entries = fs.readdirSync(dir, { withFileTypes: true });

  for (const entry of entries) {
    const fullPath = path.join(dir, entry.name);

    // Skip excluded directories
    if (entry.isDirectory() && EXCLUDE_DIRS.includes(entry.name)) {
      continue;
    }

    if (entry.isDirectory()) {
      scanDirectory(fullPath, results);
    } else if (entry.isFile() && /\.(js|jsx|ts|tsx)$/.test(entry.name)) {
      const fileResults = scanFile(fullPath);
      results.push(...fileResults);
    }
  }

  return results;
}

// Function to update imports in a file
function updateImports(file, changes) {
  try {
    let content = fs.readFileSync(file, 'utf8');

    // Apply changes in reverse order to avoid offset issues
    changes.sort((a, b) => b.oldImport.index - a.oldImport.index);

    for (const change of changes) {
      content = content.replace(change.oldImport, change.newImport);
    }

    fs.writeFileSync(file, content, 'utf8');
    console.log(`Updated ${changes.length} imports in ${file}`);

    return true;
  } catch (error) {
    console.error(`Error updating file ${file}:`, error.message);
    return false;
  }
}

// Main function
function main() {
  console.log('Scanning for imports to update...');

  const allResults = [];

  // Scan directories
  for (const dir of SCAN_DIRS) {
    const dirPath = path.join(ROOT_DIR, dir);

    if (fs.existsSync(dirPath)) {
      console.log(`Scanning ${dir}...`);
      const results = scanDirectory(dirPath);
      allResults.push(...results);
    } else {
      console.log(`Directory ${dir} not found, skipping.`);
    }
  }

  // Group results by file
  const fileGroups = {};
  for (const result of allResults) {
    if (!fileGroups[result.file]) {
      fileGroups[result.file] = [];
    }
    fileGroups[result.file].push(result);
  }

  // Print results
  console.log('\nFound imports to update:');
  console.log('========================\n');

  for (const [file, changes] of Object.entries(fileGroups)) {
    console.log(`File: ${file}`);

    for (const change of changes) {
      console.log(`  Line ${change.line}:`);
      console.log(`    Old: ${change.oldImport}`);
      console.log(`    New: ${change.newImport}`);
      console.log();
    }
  }

  // Ask for confirmation
  console.log(`Found ${allResults.length} imports to update in ${Object.keys(fileGroups).length} files.`);
  console.log('To update imports, run this script with the --update flag.');

  // Update if --update flag is provided
  if (process.argv.includes('--update')) {
    console.log('\nUpdating imports...');

    let successCount = 0;
    let failCount = 0;

    for (const [file, changes] of Object.entries(fileGroups)) {
      const success = updateImports(file, changes);
      if (success) {
        successCount++;
      } else {
        failCount++;
      }
    }

    console.log(`\nUpdate complete: ${successCount} files updated, ${failCount} files failed.`);
  }
}

// Run the script
main();"}

