          >
            <Text style={{ color: colors.onPrimary }}>Try Again</Text>
          </TouchableOpacity>
        </View>
      );
    }

    if (!user) {
      return (
        <View style={styles.centerContent}>
          <Text style={{ color: colors.text }}>No user found</Text>
        </View>
      );
    }

    return (
