    }
    button:hover {
      background-color: #0055aa;
    }
    .log-entry {
      margin: 5px 0;
      padding: 5px;
      border-bottom: 1px solid #eee;
    }
    #summary {
      margin-top: 30px;
      background-color: #f0f8ff;
      padding: 15px;
      border-radius: 5px;
      border-left: 4px solid #0066cc;
    }
  </style>
</head>
<body>
  <h1>Firebase GoDaddy Diagnostics</h1>
  <p>This tool helps diagnose issues with Firebase on GoDaddy hosting. Run the tests below to identify potential problems.</p>

  <div class="test-section">
    <div class="test-title">1. Basic Environment Tests</div>
    <div class="test-description">Checks basic JavaScript functionality and browser capabilities.</div>
    <button onclick="runEnvironmentTests()">Run Environment Tests</button>
    <div id="environment-results" class="test-results"></div>
  </div>

  <div class="test-section">
    <div class="test-title">2. Network Connectivity Tests</div>
    <div class="test-description">Checks connectivity to Firebase domains and services.</div>
    <button onclick="runNetworkTests()">Run Network Tests</button>
    <div id="network-results" class="test-results"></div>
  </div>

  <div class="test-section">
    <div class="test-title">3. Content Security Policy (CSP) Tests</div>
    <div class="test-description">Checks if CSP is blocking Firebase resources.</div>
    <button onclick="runCSPTests()">Run CSP Tests</button>
    <div id="csp-results" class="test-results"></div>
  </div>

  <div class="test-section">
    <div class="test-title">4. Firebase Configuration Tests</div>
    <div class="test-description">Checks if Firebase configuration is properly loaded.</div>
    <button onclick="runConfigTests()">Run Configuration Tests</button>
    <div id="config-results" class="test-results"></div>
  </div>

  <div class="test-section">
    <div class="test-title">5. Firebase SDK Tests</div>
    <div class="test-description">Attempts to load and initialize Firebase SDK.</div>
    <button onclick="runSDKTests()">Run SDK Tests</button>
    <div id="sdk-results" class="test-results"></div>
  </div>

  <div id="summary">
    <h2>Diagnostic Summary</h2>
    <p>Run all tests above to generate a summary of issues.</p>
    <div id="summary-results"></div>
  </div>

  <script>
    // Utility functions
    function logResult(containerId, message, type = 'info') {
      const container = document.getElementById(containerId);
      const logEntry = document.createElement('div');
      logEntry.className = `log-entry ${type}`;
      logEntry.textContent = message;
      container.appendChild(logEntry);

      // Also update summary
      updateSummary(message, type);
    }

    function updateSummary(message, type) {
      const summaryResults = document.getElementById('summary-results');

      // Only add critical information to summary
      if (type === 'error' || type === 'warning' || message.includes('Summary')) {
        const summaryEntry = document.createElement('div');
        summaryEntry.className = `log-entry ${type}`;
        summaryEntry.textContent = message;
        summaryResults.appendChild(summaryEntry);
      }
    }

    // Test functions
    function runEnvironmentTests() {
      const resultsContainer = document.getElementById('environment-results');
      resultsContainer.innerHTML = ''; // Clear previous results

      // Check JavaScript
      try {
        logResult('environment-results', '✓ JavaScript is working properly', 'success');
      } catch (e) {
        logResult('environment-results', '✗ JavaScript error: ' + e.message, 'error');
      }

      // Check browser
      logResult('environment-results', '✓ Browser: ' + navigator.userAgent, 'success');

      // Check localStorage
      try {
        localStorage.setItem('test', 'test');
        localStorage.removeItem('test');
        logResult('environment-results', '✓ localStorage is available', 'success');
      } catch (e) {
        logResult('environment-results', '✗ localStorage is not available: ' + e.message, 'error');
      }

      // Check if running on GoDaddy
      const isGoDaddy = 
        document.cookie.indexOf('GoDaddy') !== -1 ||
        navigator.userAgent.indexOf('GoDaddy') !== -1 ||
        (typeof window.location !== 'undefined' && 
         window.location.hostname.indexOf('secureserver.net') !== -1);

      if (isGoDaddy) {
        logResult('environment-results', '✓ Running on GoDaddy hosting', 'success');
      } else {
        logResult('environment-results', 'ℹ Not running on GoDaddy hosting', 'warning');
      }

      logResult('environment-results', 'Environment Tests Summary: Basic environment checks completed', 'success');
    }

    function runNetworkTests() {
      const resultsContainer = document.getElementById('network-results');
      resultsContainer.innerHTML = ''; // Clear previous results

      logResult('network-results', 'Starting network connectivity tests...', 'info');

      // Test domains
      const domains = [
        { url: 'https://www.googleapis.com', name: 'Google APIs' },
        { url: 'https://firebaseinstallations.googleapis.com', name: 'Firebase Installations' },
        { url: 'https://firestore.googleapis.com', name: 'Firestore' },
        { url: 'https://identitytoolkit.googleapis.com', name: 'Firebase Auth' },
        { url: 'https://firebasestorage.googleapis.com', name: 'Firebase Storage' },
        { url: 'https://www.gstatic.com', name: 'Google Static Content' }
      ];

      let completedTests = 0;

      domains.forEach(domain => {
        const img = new Image();
        const timestamp = new Date().getTime();

        img.onload = () => {
          logResult('network-results', `✓ Connection to ${domain.name} (${domain.url}): Success`, 'success');
          completedTests++;
          checkAllComplete();
        };

        img.onerror = () => {
          logResult('network-results', `✗ Connection to ${domain.name} (${domain.url}): Failed`, 'error');
          completedTests++;
          checkAllComplete();
        };

        // Add cache-busting parameter and attempt to load a small image
        img.src = `${domain.url}/favicon.ico?nocache=${timestamp}`;

        // Set a timeout in case the request hangs
        setTimeout(() => {
          if (img.complete === false) {
            logResult('network-results', `⚠ Connection to ${domain.name} (${domain.url}): Timeout`, 'warning');
            completedTests++;
            checkAllComplete();
          }
        }, 5000);
      });

      function checkAllComplete() {
        if (completedTests >= domains.length) {
          logResult('network-results', 'Network Tests Summary: ' + completedTests + ' of ' + domains.length + ' tests completed', 'info');
        }
      }
    }

    function runCSPTests() {
      const resultsContainer = document.getElementById('csp-results');
      resultsContainer.innerHTML = ''; // Clear previous results

      logResult('csp-results', 'Starting Content Security Policy tests...', 'info');

      // Check for CSP meta tag
      const cspMeta = document.querySelector('meta[http-equiv="Content-Security-Policy"]');
      if (cspMeta) {
        const cspContent = cspMeta.getAttribute('content');
        logResult('csp-results', '⚠ CSP meta tag found: ' + cspContent, 'warning');

        // Check if CSP allows Firebase domains
        const requiredDomains = [
          'googleapis.com',
          'firebaseinstallations.googleapis.com',
          'firestore.googleapis.com',
          'identitytoolkit.googleapis.com',
          'firebasestorage.googleapis.com',
          'www.gstatic.com'
        ];

        let missingDomains = [];

        requiredDomains.forEach(domain => {
          if (cspContent.indexOf(domain) === -1) {
            missingDomains.push(domain);
          }
        });

        if (missingDomains.length > 0) {
          logResult('csp-results', '✗ CSP is missing these required domains: ' + missingDomains.join(', '), 'error');
        } else {
          logResult('csp-results', '✓ CSP includes all required Firebase domains', 'success');
        }

        // Check for unsafe-inline (needed for some Firebase operations)
        if (cspContent.indexOf('unsafe-inline') === -1) {
          logResult('csp-results', '⚠ CSP does not allow unsafe-inline which may be needed for Firebase', 'warning');
        } else {
          logResult('csp-results', '✓ CSP allows unsafe-inline', 'success');
        }
      } else {
        logResult('csp-results', '✓ No CSP meta tag found (this is good for testing)', 'success');
      }

      // Test dynamic script injection (which CSP might block)
      try {
        const testScript = document.createElement('script');
        testScript.textContent = 'window.cspTestPassed = true;';
        document.head.appendChild(testScript);

        setTimeout(() => {
          if (window.cspTestPassed) {
            logResult('csp-results', '✓ Dynamic script injection works (not blocked by CSP)', 'success');
          } else {
            logResult('csp-results', '✗ Dynamic script injection failed (might be blocked by CSP)', 'error');
          }

          logResult('csp-results', 'CSP Tests Summary: Content Security Policy tests completed', 'info');
        }, 500);
      } catch (e) {
        logResult('csp-results', '✗ Error testing dynamic script injection: ' + e.message, 'error');
        logResult('csp-results', 'CSP Tests Summary: Content Security Policy tests completed with errors', 'error');
      }
    }

    function runConfigTests() {
      const resultsContainer = document.getElementById('config-results');
      resultsContainer.innerHTML = ''; // Clear previous results

      logResult('config-results', 'Starting Firebase configuration tests...', 'info');

      // Check if firebase-config.js is loaded
      if (typeof window.FIREBASE_CONFIG !== 'undefined') {
        logResult('config-results', '✓ Firebase configuration found', 'success');

        // Check required fields
        const requiredFields = ['apiKey', 'authDomain', 'projectId'];
        let missingFields = [];

