  <title>Your Website Title</title>

  <!-- Your other meta tags and CSS links go here -->

  <!-- Firebase SDK Scripts -->
  <script src="https://www.gstatic.com/firebasejs/9.6.1/firebase-app-compat.js"></script>
  <script src="https://www.gstatic.com/firebasejs/9.6.1/firebase-firestore-compat.js"></script>
  <script src="https://www.gstatic.com/firebasejs/9.6.1/firebase-auth-compat.js"></script>

  <!-- Firebase Initialization with Diagnostic Logging -->
  <script>
    // Log when the script starts executing
    console.log("Firebase initialization script starting");

    try {
      // Your Firebase configuration
      const firebaseConfig = {
        apiKey: "YOUR_API_KEY",
        authDomain: "YOUR_PROJECT_ID.firebaseapp.com",
        projectId: "YOUR_PROJECT_ID",
        storageBucket: "YOUR_PROJECT_ID.appspot.com",
        messagingSenderId: "YOUR_MESSAGING_SENDER_ID",
        appId: "YOUR_APP_ID"
      };

      // Initialize Firebase with diagnostic logging
      console.log("Attempting to initialize Firebase");
      firebase.initializeApp(firebaseConfig);
      console.log("Firebase initialized successfully:", !!firebase);

      // Verify services are available
      console.log("Firestore available:", !!firebase.firestore);
      console.log("Auth available:", !!firebase.auth);

      // Add event listener for auth state changes
      firebase.auth().onAuthStateChanged(function(user) {
        if (user) {
          console.log("User is signed in:", user.uid);
        } else {
          console.log("No user is signed in");
        }
      });
    } catch (error) {
      console.error("Firebase initialization error:", error);
    }
  </script>

  <!-- Your other scripts go here -->
</head>
<body>
  <!-- Your page content goes here -->

  <!-- Optional: Add a Firebase test element -->
  <div id="firebase-status" style="display: none; padding: 10px; margin: 10px; background-color: #f0f8ff; border: 1px solid #0066cc;">
    Firebase status will appear here
  </div>

  <!-- Optional: Script to display Firebase status -->
  <script>
    // Display Firebase status after a short delay
    setTimeout(function() {
      const statusElement = document.getElementById('firebase-status');
      if (statusElement) {
        try {
          if (firebase && firebase.app) {
            statusElement.textContent = "Firebase is initialized and working correctly!";
            statusElement.style.backgroundColor = "#e6ffe6";
            statusElement.style.border = "1px solid #00cc00";
          } else {
            statusElement.textContent = "Firebase is not initialized correctly.";
            statusElement.style.backgroundColor = "#ffe6e6";
            statusElement.style.border = "1px solid #cc0000";
          }
          statusElement.style.display = "block";
        } catch (error) {
          statusElement.textContent = "Error checking Firebase status: " + error.message;
          statusElement.style.backgroundColor = "#ffe6e6";
          statusElement.style.border = "1px solid #cc0000";
          statusElement.style.display = "block";
        }
      }
    }, 2000);
  </script>
</body>
</html>"}

