/**
 * Firebase Diagnostic Script
 * 
 * This script tests Firebase connectivity and initialization.
 * Add this script to your page after Firebase initialization to diagnose issues.
 */

(function() {
  // Create a container for diagnostic results
  function createDiagnosticContainer() {
    const container = document.createElement('div');
    container.id = 'firebase-diagnostic-container';
    container.style.position = 'fixed';
    container.style.bottom = '20px';
    container.style.right = '20px';
    container.style.width = '300px';
    container.style.maxHeight = '400px';
    container.style.overflowY = 'auto';
    container.style.backgroundColor = '#f8f9fa';
    container.style.border = '1px solid #dee2e6';
    container.style.borderRadius = '4px';
    container.style.padding = '15px';
    container.style.boxShadow = '0 0.5rem 1rem rgba(0, 0, 0, 0.15)';
    container.style.zIndex = '9999';
    container.style.fontSize = '14px';
    container.style.fontFamily = 'Arial, sans-serif';

    // Add header
    const header = document.createElement('div');
    header.style.fontWeight = 'bold';
    header.style.marginBottom = '10px';
    header.style.borderBottom = '1px solid #dee2e6';
    header.style.paddingBottom = '5px';
    header.textContent = 'Firebase Diagnostics';
    container.appendChild(header);

    // Add close button
    const closeButton = document.createElement('button');
    closeButton.textContent = 'Close';
    closeButton.style.position = 'absolute';
    closeButton.style.top = '10px';
    closeButton.style.right = '10px';
    closeButton.style.padding = '2px 5px';
    closeButton.style.fontSize = '12px';
    closeButton.style.cursor = 'pointer';
    closeButton.onclick = function() {
      document.body.removeChild(container);
    };
    container.appendChild(closeButton);

    // Add results container
    const results = document.createElement('div');
    results.id = 'firebase-diagnostic-results';
    container.appendChild(results);

    return container;
  }

  // Log diagnostic message
  function logDiagnostic(message, isSuccess = true) {
    const resultsContainer = document.getElementById('firebase-diagnostic-results');
    if (!resultsContainer) return;

    const entry = document.createElement('div');
    entry.style.padding = '5px 0';
    entry.style.borderBottom = '1px solid #eee';
    entry.style.color = isSuccess ? '#28a745' : '#dc3545';

    const timestamp = new Date().toLocaleTimeString();
    entry.textContent = `[${timestamp}] ${message}`;

    resultsContainer.appendChild(entry);
  }

  // Run Firebase diagnostics
  function runDiagnostics() {
    // Check if Firebase is defined
    if (typeof firebase === 'undefined') {
      logDiagnostic('Firebase is not defined. The Firebase SDK may not be loaded correctly.', false);
      return;
    }

    logDiagnostic('Firebase SDK is loaded');

    // Check Firebase app initialization
    try {
      const app = firebase.app();
      logDiagnostic('Firebase app is initialized');

      // Log Firebase SDK version if available
      if (firebase.SDK_VERSION) {
        logDiagnostic(`Firebase SDK version: ${firebase.SDK_VERSION}`);
      }

      // Check Firebase services
      if (typeof firebase.auth !== 'undefined') {
        logDiagnostic('Firebase Auth is available');

        // Check auth state
        firebase.auth().onAuthStateChanged(function(user) {
          if (user) {
            logDiagnostic(`User is signed in: ${user.uid}`);
          } else {
            logDiagnostic('No user is signed in');
          }
        });
      } else {
        logDiagnostic('Firebase Auth is not available', false);
      }

      if (typeof firebase.firestore !== 'undefined') {
        logDiagnostic('Firebase Firestore is available');

        // Test Firestore connectivity
        firebase.firestore().collection('_diagnostics_test')
          .limit(1)
          .get()
          .then(function() {
            logDiagnostic('Firestore connection successful');
          })
          .catch(function(error) {
            logDiagnostic(`Firestore connection error: ${error.message}`, false);
          });
      } else {
        logDiagnostic('Firebase Firestore is not available', false);
      }

      // Check other Firebase services as needed
      if (typeof firebase.storage !== 'undefined') {
        logDiagnostic('Firebase Storage is available');
      }

      if (typeof firebase.functions !== 'undefined') {
        logDiagnostic('Firebase Functions is available');
      }

    } catch (error) {
      logDiagnostic(`Firebase initialization error: ${error.message}`, false);
    }
  }

  // Initialize diagnostics
  function initDiagnostics() {
    // Wait for DOM to be ready
    if (document.readyState === 'loading') {
      document.addEventListener('DOMContentLoaded', function() {
        const container = createDiagnosticContainer();
        document.body.appendChild(container);

        // Run diagnostics after a short delay to allow Firebase to initialize
        setTimeout(runDiagnostics, 1000);
      });
    } else {
      const container = createDiagnosticContainer();
      document.body.appendChild(container);

      // Run diagnostics after a short delay to allow Firebase to initialize
      setTimeout(runDiagnostics, 1000);
    }
  }

  // Start diagnostics
  initDiagnostics();
})();
