(function() {
  // Create verification UI
  function createVerificationUI() {
    // Create container
    const container = document.createElement('div');
    container.id = 'verification-container';
    container.style.position = 'fixed';
    container.style.top = '20px';
    container.style.left = '20px';
    container.style.width = '400px';
    container.style.maxHeight = '80vh';
    container.style.overflowY = 'auto';
    container.style.backgroundColor = '#f8f9fa';
    container.style.border = '1px solid #dee2e6';
    container.style.borderRadius = '4px';
    container.style.padding = '15px';
    container.style.boxShadow = '0 0.5rem 1rem rgba(0, 0, 0, 0.15)';
    container.style.zIndex = '9999';
    container.style.fontSize = '14px';
    container.style.fontFamily = 'Arial, sans-serif';

    // Add header
    const header = document.createElement('div');
    header.style.fontWeight = 'bold';
    header.style.marginBottom = '10px';
    header.style.borderBottom = '1px solid #dee2e6';
    header.style.paddingBottom = '5px';
    header.textContent = 'Fix Verification';
    container.appendChild(header);

    // Add close button
    const closeButton = document.createElement('button');
    closeButton.textContent = 'Close';
    closeButton.style.position = 'absolute';
    closeButton.style.top = '10px';
    closeButton.style.right = '10px';
    closeButton.style.padding = '2px 5px';
    closeButton.style.fontSize = '12px';
    closeButton.style.cursor = 'pointer';
    closeButton.onclick = function() {
      document.body.removeChild(container);
    };
    container.appendChild(closeButton);

    // Add results container
    const results = document.createElement('div');
    results.id = 'verification-results';
    container.appendChild(results);

    // Add run button
    const runButton = document.createElement('button');
    runButton.textContent = 'Run Verification';
    runButton.style.marginTop = '10px';
    runButton.style.padding = '5px 10px';
    runButton.style.backgroundColor = '#0066cc';
    runButton.style.color = 'white';
    runButton.style.border = 'none';
    runButton.style.borderRadius = '4px';
    runButton.style.cursor = 'pointer';
    runButton.onclick = runVerification;
    container.appendChild(runButton);

    return container;
  }

  // Log verification result
  function logResult(message, status) {
    const resultsContainer = document.getElementById('verification-results');
    if (!resultsContainer) return;

    const entry = document.createElement('div');
    entry.style.padding = '5px 0';
    entry.style.borderBottom = '1px solid #eee';

    // Set color based on status
    switch (status) {
      case 'success':
        entry.style.color = '#28a745';
        break;
      case 'error':
        entry.style.color = '#dc3545';
        break;
      case 'warning':
        entry.style.color = '#ffc107';
        break;
      case 'info':
      default:
        entry.style.color = '#0066cc';
        break;
    }

    const timestamp = new Date().toLocaleTimeString();
    entry.textContent = `[${timestamp}] ${message}`;

    resultsContainer.appendChild(entry);
  }

  // Run verification tests
  function runVerification() {
    const resultsContainer = document.getElementById('verification-results');
    if (resultsContainer) {
      resultsContainer.innerHTML = '';
    }

    logResult('Starting verification...', 'info');

    // Verify 500 error is fixed
    logResult('Checking if 500 error is fixed...', 'info');
    // We can't directly check for 500 errors, but if the page loads, it's a good sign
    logResult('Page loaded successfully, which suggests 500 error is fixed', 'success');

    // Verify CSP is correctly configured
    logResult('Checking Content Security Policy...', 'info');
    const cspMeta = document.querySelector('meta[http-equiv="Content-Security-Policy"]');
    if (cspMeta) {
      const cspContent = cspMeta.getAttribute('content');

      // Check if CSP includes Firebase domains
      const requiredDomains = [
        'googleapis.com',
        'gstatic.com',
        'unsafe-inline',
        'unsafe-eval'
      ];

      let missingDomains = [];
      for (const domain of requiredDomains) {
        if (!cspContent.includes(domain)) {
          missingDomains.push(domain);
        }
      }

      if (missingDomains.length === 0) {
        logResult('CSP is correctly configured with all required domains', 'success');
      } else {
        logResult(`CSP is missing these required domains: ${missingDomains.join(', ')}`, 'error');
      }
    } else {
      logResult('No CSP meta tag found', 'error');
    }

    // Verify Firebase is loaded
    logResult('Checking if Firebase is loaded...', 'info');
    if (typeof firebase !== 'undefined') {
      logResult('Firebase SDK is loaded', 'success');

      // Verify Firebase is initialized
      try {
        const app = firebase.app();
        logResult('Firebase is initialized', 'success');

        // Log Firebase SDK version
        if (firebase.SDK_VERSION) {
          logResult(`Firebase SDK version: ${firebase.SDK_VERSION}`, 'info');
        }

        // Check Firebase services
        if (typeof firebase.auth !== 'undefined') {
          logResult('Firebase Auth is available', 'success');
        } else {
          logResult('Firebase Auth is not available', 'warning');
        }

        if (typeof firebase.firestore !== 'undefined') {
          logResult('Firebase Firestore is available', 'success');

          // Test Firestore connectivity
          firebase.firestore().collection('_verification_test')
            .limit(1)
            .get()
            .then(function() {
              logResult('Firestore connection successful', 'success');
              logResult('All verification tests passed!', 'success');
            })
            .catch(function(error) {
              logResult(`Firestore connection error: ${error.message}`, 'error');
              logResult('Some verification tests failed', 'warning');
            });
        } else {
          logResult('Firebase Firestore is not available', 'warning');
          logResult('Some verification tests passed, but Firestore is not available', 'warning');
        }
      } catch (error) {
        logResult(`Firebase initialization error: ${error.message}`, 'error');
        logResult('Verification failed: Firebase is not properly initialized', 'error');
      }
    } else {
      logResult('Firebase SDK is not loaded', 'error');
      logResult('Verification failed: Firebase SDK is not loaded', 'error');

      // Check if Firebase scripts are in the page
      const firebaseScripts = Array.from(document.querySelectorAll('script')).filter(script => 
        script.src && (script.src.includes('firebase') || script.src.includes('gstatic.com'))
      );

      if (firebaseScripts.length > 0) {
        logResult('Firebase scripts are in the page but not loading properly', 'warning');
        logResult('This might be due to CSP issues or script loading order', 'warning');
      } else {
        logResult('No Firebase scripts found in the page', 'error');
      }
    }

    // Check console for errors
    logResult('Checking console for errors...', 'info');
    if (typeof console.error.toString === 'function') {
      // Create a proxy for console.error to capture errors
      const originalConsoleError = console.error;
      let errors = [];

      console.error = function() {
        errors.push(Array.from(arguments).join(' '));
        originalConsoleError.apply(console, arguments);
      };

      // Restore original console.error after a short delay
      setTimeout(function() {
        console.error = originalConsoleError;

        if (errors.length === 0) {
          logResult('No console errors detected', 'success');
        } else {
          logResult(`${errors.length} console errors detected`, 'warning');
          errors.forEach(error => {
            logResult(`Console error: ${error}`, 'warning');
          });
        }
      }, 2000);
    } else {
      logResult('Unable to check console for errors', 'warning');
    }
  }

  // Initialize verification
  function initVerification() {
    // Wait for DOM to be ready
    if (document.readyState === 'loading') {
      document.addEventListener('DOMContentLoaded', function() {
        const container = createVerificationUI();
        document.body.appendChild(container);
      });
    } else {
      const container = createVerificationUI();
      document.body.appendChild(container);
    }
  }

  // Start verification
  initVerification();
})();"}

