 * Firebase Authentication Diagnostic Script
 * Created by Roo for AI Sports Edge
 * 
 * This script diagnoses Firebase authentication issues, specifically API key problems.
 */

(function() {
  // Create diagnostic UI
  function createDiagnosticUI() {
    // Create container
    const container = document.createElement('div');
    container.id = 'firebase-auth-diagnostic-container';
    container.style.position = 'fixed';
    container.style.top = '20px';
    container.style.left = '20px';
    container.style.width = '400px';
    container.style.maxHeight = '80vh';
    container.style.overflowY = 'auto';
    container.style.backgroundColor = '#f8f9fa';
    container.style.border = '1px solid #dee2e6';
    container.style.borderRadius = '4px';
    container.style.padding = '15px';
    container.style.boxShadow = '0 0.5rem 1rem rgba(0, 0, 0, 0.15)';
    container.style.zIndex = '9999';
    container.style.fontSize = '14px';
    container.style.fontFamily = 'Arial, sans-serif';

    // Add header
    const header = document.createElement('div');
    header.style.fontWeight = 'bold';
    header.style.marginBottom = '10px';
    header.style.borderBottom = '1px solid #dee2e6';
    header.style.paddingBottom = '5px';
    header.textContent = 'Firebase Authentication Diagnostic';
    container.appendChild(header);

    // Add close button
    const closeButton = document.createElement('button');
    closeButton.textContent = 'Close';
    closeButton.style.position = 'absolute';
    closeButton.style.top = '10px';
    closeButton.style.right = '10px';
    closeButton.style.padding = '2px 5px';
    closeButton.style.fontSize = '12px';
    closeButton.style.cursor = 'pointer';
    closeButton.onclick = function() {
      document.body.removeChild(container);
    };
    container.appendChild(closeButton);

    // Add results container
    const results = document.createElement('div');
    results.id = 'firebase-auth-diagnostic-results';
    container.appendChild(results);

    // Add run button
    const runButton = document.createElement('button');
    runButton.textContent = 'Run Diagnostic';
    runButton.style.marginTop = '10px';
    runButton.style.padding = '5px 10px';
    runButton.style.backgroundColor = '#0066cc';
    runButton.style.color = 'white';
    runButton.style.border = 'none';
    runButton.style.borderRadius = '4px';
    runButton.style.cursor = 'pointer';
    runButton.onclick = runDiagnostic;
    container.appendChild(runButton);

    return container;
  }

  // Log diagnostic message
  function logMessage(message, status) {
    const resultsContainer = document.getElementById('firebase-auth-diagnostic-results');
    if (!resultsContainer) return;

    const entry = document.createElement('div');
    entry.style.padding = '5px 0';
    entry.style.borderBottom = '1px solid #eee';

    // Set color based on status
    switch (status) {
      case 'success':
        entry.style.color = '#28a745';
        break;
      case 'error':
        entry.style.color = '#dc3545';
        break;
      case 'warning':
        entry.style.color = '#ffc107';
        break;
      case 'info':
      default:
        entry.style.color = '#0066cc';
        break;
    }

    const timestamp = new Date().toLocaleTimeString();
    entry.textContent = `[${timestamp}] ${message}`;

    resultsContainer.appendChild(entry);
  }

  // Run diagnostic tests
  function runDiagnostic() {
    const resultsContainer = document.getElementById('firebase-auth-diagnostic-results');
    if (resultsContainer) {
      resultsContainer.innerHTML = '';
    }

    logMessage('Starting Firebase Authentication diagnostic...', 'info');

    // Check if Firebase is defined
    if (typeof firebase === 'undefined') {
      logMessage('Firebase SDK is not loaded. Cannot proceed with authentication diagnostic.', 'error');
      return;
    }

    logMessage('Firebase SDK is loaded', 'success');

    // Check Firebase version
    if (firebase.SDK_VERSION) {
      logMessage(`Firebase SDK version: ${firebase.SDK_VERSION}`, 'info');
    }

    // Check if Firebase is initialized
    try {
      const app = firebase.app();
      logMessage('Firebase is initialized', 'success');

      // Check Firebase config
      logMessage('Checking Firebase configuration...', 'info');

      // Get Firebase config
      const config = app.options;

      // Check API key
      if (config.apiKey) {
        // Mask API key for security
        const maskedApiKey = config.apiKey.substring(0, 4) + '...' + config.apiKey.substring(config.apiKey.length - 4);
        logMessage(`API Key is present: ${maskedApiKey}`, 'success');

        // Check if API key is valid format (starts with "AIza")
        if (!config.apiKey.startsWith('AIza')) {
          logMessage('API Key format is invalid. Firebase API keys should start with "AIza"', 'error');
        }
      } else {
        logMessage('API Key is missing from Firebase configuration', 'error');
      }

      // Check auth domain
      if (config.authDomain) {
        logMessage(`Auth Domain is present: ${config.authDomain}`, 'success');
      } else {
        logMessage('Auth Domain is missing from Firebase configuration', 'error');
      }

      // Check project ID
      if (config.projectId) {
        logMessage(`Project ID is present: ${config.projectId}`, 'success');
      } else {
        logMessage('Project ID is missing from Firebase configuration', 'error');
      }

      // Check if auth is available
      if (typeof firebase.auth !== 'undefined') {
        logMessage('Firebase Auth is available', 'success');

        // Test anonymous auth (this won't create an actual user)
        logMessage('Testing Firebase Authentication API...', 'info');

        firebase.auth().signInAnonymously()
          .then(() => {
            logMessage('Anonymous authentication successful. API key is valid.', 'success');
            firebase.auth().signOut(); // Sign out after successful test
          })
          .catch((error) => {
            logMessage(`Authentication error: ${error.code} - ${error.message}`, 'error');

            // Specific error handling
            if (error.code === 'auth/api-key-not-valid') {
              logMessage('API key is not valid. This could be due to:', 'error');
              logMessage('1. Incorrect API key in Firebase configuration', 'error');
              logMessage('2. API key restrictions in Firebase console', 'error');
              logMessage('3. API key has been revoked or disabled', 'error');

              // Suggest solutions
              logMessage('\nPossible solutions:', 'info');
              logMessage('1. Verify the API key in your Firebase configuration', 'info');
              logMessage('2. Check API key restrictions in Firebase console', 'info');
              logMessage('3. Create a new API key in Firebase console', 'info');
              logMessage('4. Ensure the domain is authorized in Firebase console', 'info');
            }
          });
      } else {
        logMessage('Firebase Auth is not available', 'error');
      }

      // Check domain authorization
      const currentDomain = window.location.hostname;
      logMessage(`Current domain: ${currentDomain}`, 'info');
      logMessage('Ensure this domain is authorized in Firebase console:', 'info');
      logMessage('1. Go to Firebase console > Project settings', 'info');
      logMessage('2. Go to "Your apps" section', 'info');
      logMessage('3. Select your web app', 'info');
      logMessage('4. Check "Authorized domains" section', 'info');
      logMessage(`5. Ensure "${currentDomain}" is in the list`, 'info');

    } catch (error) {
      logMessage(`Firebase initialization error: ${error.message}`, 'error');
    }

    // Check for environment variables
    logMessage('\nChecking for environment variables...', 'info');
    if (typeof process !== 'undefined' && process.env) {
      if (process.env.FIREBASE_API_KEY) {
        logMessage('FIREBASE_API_KEY environment variable is present', 'success');
      } else {
        logMessage('FIREBASE_API_KEY environment variable is not present', 'warning');
      }
    } else {
      logMessage('Environment variables are not accessible in browser context', 'info');
    }

    // Check CSP for authentication domains
    logMessage('\nChecking Content Security Policy for authentication domains...', 'info');
    const cspMeta = document.querySelector('meta[http-equiv="Content-Security-Policy"]');
    if (cspMeta) {
      const cspContent = cspMeta.getAttribute('content');

      // Check for required domains
      const requiredDomains = [
        'identitytoolkit.googleapis.com',
        'securetoken.googleapis.com',
        'accounts.google.com'
      ];

      let missingDomains = [];
      for (const domain of requiredDomains) {
        if (!cspContent.includes(domain)) {
          missingDomains.push(domain);
        }
      }

      if (missingDomains.length === 0) {
        logMessage('CSP includes all required authentication domains', 'success');
      } else {
        logMessage(`CSP is missing these required authentication domains: ${missingDomains.join(', ')}`, 'error');

        // Suggest CSP update
        logMessage('\nUpdate your CSP to include these domains:', 'info');
        logMessage(`connect-src 'self' ... ${missingDomains.join(' ')} ...`, 'info');
      }
    } else {
      logMessage('No CSP meta tag found', 'warning');
    }
  }

  // Initialize diagnostic
  function initDiagnostic() {
    // Wait for DOM to be ready
    if (document.readyState === 'loading') {
      document.addEventListener('DOMContentLoaded', function() {
        const container = createDiagnosticUI();
        document.body.appendChild(container);
      });
    } else {
      const container = createDiagnosticUI();
      document.body.appendChild(container);
    }
  }

  // Start diagnostic
  initDiagnostic();
})();"}

