        const config = app.options;

        // Mask API key for security
        const maskedConfig = { ...config };
        if (maskedConfig.apiKey) {
          maskedConfig.apiKey = maskedConfig.apiKey.substring(0, 4) + '...' + maskedConfig.apiKey.substring(maskedConfig.apiKey.length - 4);
        }

        configDisplay.textContent = JSON.stringify(maskedConfig, null, 2);

        // Pre-fill form fields
        if (config.authDomain) {
          document.getElementById('auth-domain-input').value = config.authDomain;
