 * Fix for Firebase Authentication API Key Issue
 * 
 * This script fixes the "auth/api-key-not-valid" error by updating the Content Security Policy
 * to include 'unsafe-eval' in the script-src directive, which is required by Firebase Authentication.
 */

(function() {
  console.log('[Firebase Auth Fix] Script loaded');

  // Function to update the CSP meta tag
  function updateCSP() {
    try {
      // Find the CSP meta tag
      const cspMeta = document.querySelector('meta[http-equiv="Content-Security-Policy"]');
      if (!cspMeta) {
        console.log('[Firebase Auth Fix] No CSP meta tag found');
        return;
      }

      console.log('[Firebase Auth Fix] Found CSP meta tag');

      // Get the current CSP content
      const cspContent = cspMeta.getAttribute('content');

      // Check if 'unsafe-eval' is already in the script-src directive
      if (cspContent.includes("script-src") && !cspContent.includes("'unsafe-eval'")) {
        console.log('[Firebase Auth Fix] CSP is missing unsafe-eval, updating...');

        // Add 'unsafe-eval' to the script-src directive
        const updatedCSP = cspContent.replace(
          /(script-src [^;]*)/,
          "$1 'unsafe-eval'"
        );

        // Update the CSP meta tag
        cspMeta.setAttribute('content', updatedCSP);
        console.log('[Firebase Auth Fix] CSP updated to include unsafe-eval');

        // Log the updated CSP
        console.log('[Firebase Auth Fix] Updated CSP:', updatedCSP);
      } else if (cspContent.includes("'unsafe-eval'")) {
        console.log('[Firebase Auth Fix] CSP already includes unsafe-eval');
      } else {
        console.log('[Firebase Auth Fix] Could not find script-src directive in CSP');
      }
    } catch (error) {
      console.error('[Firebase Auth Fix] Error updating CSP:', error);
    }
  }

  // Function to verify Firebase configuration
  function verifyFirebaseConfig() {
    try {
      // Check if Firebase config is available
      if (typeof window.FIREBASE_CONFIG === 'undefined') {
        console.log('[Firebase Auth Fix] Firebase config not found');
        return;
      }

      console.log('[Firebase Auth Fix] Firebase config found');

      // Verify API key
      const apiKey = window.FIREBASE_CONFIG.apiKey;
      if (!apiKey) {
        console.log('[Firebase Auth Fix] API key is missing');
        return;
      }

      // Mask API key for security
      const maskedApiKey = apiKey.substring(0, 4) + '...' + apiKey.substring(apiKey.length - 4);
      console.log('[Firebase Auth Fix] API key is present:', maskedApiKey);

      // Verify auth domain
      const authDomain = window.FIREBASE_CONFIG.authDomain;
      if (!authDomain) {
        console.log('[Firebase Auth Fix] Auth domain is missing');
        return;
      }

      console.log('[Firebase Auth Fix] Auth domain is present:', authDomain);

      // Verify project ID
      const projectId = window.FIREBASE_CONFIG.projectId;
      if (!projectId) {
        console.log('[Firebase Auth Fix] Project ID is missing');
        return;
      }

      console.log('[Firebase Auth Fix] Project ID is present:', projectId);

      // Firebase config is valid
      console.log('[Firebase Auth Fix] Firebase configuration is valid');
